// src/App.tsx
import { useEffect } from 'react';
import './App.css';
import { Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { useDispatch } from 'react-redux';

import { PublicRoutes } from './routes/PublicRoutes';
import { CustomerRoutes } from './routes/CustomerRoutes';
import { ManagerRoutes } from './routes/ManagerRoutes';
import { AdminRoutes } from './routes/AdminRoutes';

import Unauthorized from './views/auth/Unauthorized';
import ScrollToTop from './components/common/ScrollToTop';

import { AUTH_KEYS } from './constants/auth';
import api from './services/api';
import { AUTH_LOGIN_SUCCESS, AUTH_LOGOUT } from './redux/types/authTypes';

function App() {
  const dispatch = useDispatch();

  useEffect(() => {
    const token = localStorage.getItem(AUTH_KEYS.ACCESS_TOKEN);
    const refreshToken = localStorage.getItem(AUTH_KEYS.REFRESH_TOKEN);

    // Scenario: User has Refresh Token but Access Token is missing (or expired/flush)
    if (!token && refreshToken) {
      // Attempt to restore session
      api.post('/api/auth/refresh', { refreshToken })
        .then(res => {
          const { accessToken, refreshToken: newRefresh, user } = res.data;

          localStorage.setItem(AUTH_KEYS.ACCESS_TOKEN, accessToken);
          if (newRefresh) localStorage.setItem(AUTH_KEYS.REFRESH_TOKEN, newRefresh);
          if (user) localStorage.setItem(AUTH_KEYS.USER, JSON.stringify(user));

          dispatch({
            type: AUTH_LOGIN_SUCCESS,
            payload: { accessToken, user }
          });
        })
        .catch((err) => {
          console.error("Session restoration failed", err);
          // If restore fails, clear everything to be safe
          localStorage.removeItem(AUTH_KEYS.ACCESS_TOKEN);
          localStorage.removeItem(AUTH_KEYS.REFRESH_TOKEN);
          localStorage.removeItem(AUTH_KEYS.USER);
          dispatch({ type: AUTH_LOGOUT });
        });
    }
  }, [dispatch]);

  return (
    <div>
      <Toaster />
      <ScrollToTop />
      <Routes>
        {PublicRoutes}

        {/* PROTECTED ROUTES */}
        {CustomerRoutes}
        {ManagerRoutes}
        {AdminRoutes}

        {/* COMMON ROUTES */}
        <Route path="/unauthorized" element={<Unauthorized />} />

        {/* FALLBACK */}
        <Route path="*" element={<Unauthorized />} />
      </Routes>
    </div>
  );
}

export default App;

import React from 'react';
import { PATTERNS } from '../../utils/validationSchemas';
import { Check, X } from 'lucide-react';

interface PasswordStrengthIndicatorProps {
    password: string;
}

const PasswordStrengthIndicator: React.FC<PasswordStrengthIndicatorProps> = ({ password }) => {
    if (!password) return null;

    const checks = [
        {
            label: `At least ${PATTERNS.minLength} characters`,
            valid: password.length >= PATTERNS.minLength,
        },
        {
            label: 'One uppercase letter',
            valid: PATTERNS.uppercase.test(password),
        },
        {
            label: 'One lowercase letter',
            valid: PATTERNS.lowercase.test(password),
        },
        {
            label: 'One number',
            valid: PATTERNS.number.test(password),
        },
        {
            label: 'One special character',
            valid: PATTERNS.special.test(password),
        },
    ];

    const allValid = checks.every((check) => check.valid);

    if (allValid) return null;

    return (
        <div className="mt-2 space-y-1 bg-gray-50 p-3 rounded-lg border border-gray-100 animate-in fade-in slide-in-from-top-2 duration-300">
            <p className="text-xs font-medium text-gray-500 mb-2">Password Requirements:</p>
            {checks.map((check, index) => (
                <div key={index} className="flex items-center gap-2 text-xs transition-colors duration-200">
                    {check.valid ? (
                        <Check size={14} className="text-green-600" />
                    ) : (
                        <X size={14} className="text-gray-400" />
                    )}
                    <span className={check.valid ? 'text-green-700 font-medium' : 'text-gray-500'}>
                        {check.label}
                    </span>
                </div>
            ))}
        </div>
    );
};

export default PasswordStrengthIndicator;

import type { FC } from 'react';
import type { Category } from '../../types/Category';
import CategoryCard from './CategoryCard';
import EmptyState from '../common/EmptyState';
import { Search } from 'lucide-react';

interface CategoryGridProps {
  categories: Category[];
  onCategoryClick?: (id: string) => void;
}

const CategoryGrid: FC<CategoryGridProps> = ({
  categories,
  onCategoryClick,
}) => {
  if (!categories || categories.length === 0) {
    return (
      <EmptyState
        title="No Categories"
        description="We couldn't find any categories at the moment."
        icon={<Search size={48} />}
      />
    );
  }

  return (
    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-6">
      {categories.map((cat, index) => (
        <CategoryCard
          key={cat._id}
          category={cat}
          onClick={onCategoryClick}
          index={index}
        />
      ))}
    </div>
  );
};

export default CategoryGrid;

import { type FC, type ReactNode } from 'react';
import { Search } from 'lucide-react';
import Input from './Input';

interface FilterBarProps {
  searchTerm: string;
  onSearchChange: (value: string) => void;
  searchPlaceholder?: string;
  searchLabel?: string;
  children?: ReactNode;
}

const FilterBar: FC<FilterBarProps> = ({
  searchTerm,
  onSearchChange,
  searchPlaceholder = 'Search...',
  searchLabel = 'Search',
  children,
}) => {
  return (
    <div className="bg-white rounded-2xl shadow-sm border border-gray-100 mb-6 transition-all duration-300">
      <div className="p-4 border-b border-gray-100">
        <div className="flex flex-col md:flex-row justify-between items-end gap-4">
          <div className="flex-1 max-w-md w-full">
            <span className="text-[10px] font-bold text-gray-400 uppercase tracking-widest ml-1 mb-1.5 block">
              {searchLabel}
            </span>
            <Input
              placeholder={searchPlaceholder}
              value={searchTerm}
              onChange={(e) => onSearchChange(e.target.value)}
              leftIcon={<Search className="w-5 h-5" />}
            />
          </div>
          {children && (
            <div className="flex flex-wrap md:flex-nowrap gap-4 justify-end">
              {children}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default FilterBar;

import { useState, useRef, useEffect } from 'react';
import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ChevronDown, Check, Filter } from 'lucide-react';

interface Option {
  value: string;
  label: string;
  icon?: React.ReactNode;
}

interface FilterSelectProps {
  label: string;
  options: Option[];
  value: string;
  onChange: (value: string) => void;
  className?: string;
  align?: 'left' | 'right';
}

const FilterSelect = ({
  label,
  options,
  value,
  onChange,
  className = '',
  align = 'left',
}: FilterSelectProps) => {
  const [isOpen, setIsOpen] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        containerRef.current &&
        !containerRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const selectedOption =
    options.find((opt) => opt.value === value) || options[0];

  return (
    <div className={`relative ${className}`} ref={containerRef}>
      <div className="flex flex-col gap-1.5">
        <span className="text-[10px] font-bold text-gray-500 uppercase tracking-widest ml-1 drop-shadow-sm">
          {label}
        </span>
        <button
          onClick={() => setIsOpen(!isOpen)}
          className={`
            flex items-center justify-between gap-3 px-4 py-2.5
            bg-white border-2 border-gray-100
            rounded-2xl shadow-sm hover:shadow-md hover:border-green-500/30
            transition-all duration-300 group min-w-[160px] cursor-pointer
            ${isOpen ? 'ring-4 ring-green-500/10 border-green-500/50' : ''}
          `}
        >
          <div className="flex items-center gap-2.5">
            <Filter
              size={16}
              className={`transition-colors ${isOpen ? 'text-green-600' : 'text-gray-400 group-hover:text-green-500'}`}
            />
            <span className="text-sm font-bold text-gray-700">
              {selectedOption ? selectedOption.label : 'Select...'}
            </span>
          </div>
          <ChevronDown
            size={16}
            className={`text-gray-400 transition-transform duration-300 ${
              isOpen ? 'rotate-180 text-green-600' : ''
            }`}
          />
        </button>
      </div>

      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: 10, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 10, scale: 0.95 }}
            transition={{ duration: 0.2, ease: 'easeOut' }}
            className={`
              absolute ${align === 'right' ? 'right-0' : 'left-0'} top-full mt-3 z-[999]
              w-full min-w-[220px] p-2
              bg-white border-2 border-gray-100
              rounded-2xl shadow-2xl shadow-gray-200/50
              flex flex-col gap-1
            `}
          >
            {options.length > 0 ? (
              options.map((option) => {
                const isSelected = option.value === value;
                return (
                  <button
                    key={option.value}
                    onClick={() => {
                      onChange(option.value);
                      setIsOpen(false);
                    }}
                    className={`
                    w-full flex items-center justify-between
                    px-4 py-3 rounded-xl text-sm font-semibold transition-all duration-200
                    ${
                      isSelected
                        ? 'bg-green-50 text-green-700'
                        : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900 cursor-pointer'
                    }
                  `}
                  >
                    <div className="flex items-center gap-2.5">
                      {option.icon && (
                        <span
                          className={
                            isSelected ? 'text-green-600' : 'text-gray-400'
                          }
                        >
                          {option.icon}
                        </span>
                      )}
                      {option.label}
                    </div>
                    {isSelected && (
                      <motion.div
                        initial={{ scale: 0 }}
                        animate={{ scale: 1 }}
                        transition={{
                          type: 'spring',
                          stiffness: 300,
                          damping: 20,
                        }}
                      >
                        <Check size={16} className="text-green-600" />
                      </motion.div>
                    )}
                  </button>
                );
              })
            ) : (
              <div className="px-4 py-3 text-sm text-gray-400 text-center">
                No options available
              </div>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default FilterSelect;

import { type FC, useEffect } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';

interface PaginationProps {
  currentPage: number;
  totalPages: number;
  onPageChange: (page: number) => void | Promise<void>;
  className?: string;
  isLoading?: boolean;
}

const Pagination: FC<PaginationProps> = ({
  currentPage,
  totalPages,
  onPageChange,
  className = '',
  isLoading = false,
}) => {
  const scrollToTop = () => {
    // Immediate scroll (auto) feels faster for paginated data
    setTimeout(() => {
      window.scrollTo({ top: 0, left: 0, behavior: 'auto' });
    }, 50);
  };

  useEffect(() => {
    scrollToTop();
  }, [currentPage]);

  const handlePageChange = (page: number) => {
    if (isLoading || page < 1 || page > totalPages) {
      return;
    }
    onPageChange(page);
  };

  if (totalPages <= 1) {
    return null;
  }

  const pages = Array.from({ length: totalPages }, (_, i) => i + 1);

  const getPageNumbers = () => {
    if (totalPages <= 7) {
      return pages;
    }

    if (currentPage <= 4) {
      return [...pages.slice(0, 5), '...', totalPages];
    }

    if (currentPage >= totalPages - 3) {
      return [1, '...', ...pages.slice(totalPages - 5)];
    }

    return [
      1,
      '...',
      currentPage - 1,
      currentPage,
      currentPage + 1,
      '...',
      totalPages,
    ];
  };

  const pageNumbers = getPageNumbers();

  return (
    <div className={`flex items-center justify-center gap-3 ${className}`}>
      {/* Previous */}
      <button
        onClick={() => handlePageChange(currentPage - 1)}
        disabled={currentPage === 1 || isLoading}
        aria-label="Previous page"
        className="
          p-2.5 rounded-xl border border-gray-100 bg-white
          text-gray-600 hover:bg-green-50 hover:text-green-600 hover:border-green-200
          disabled:opacity-30 disabled:cursor-not-allowed
          transition-all duration-300 shadow-sm hover:shadow-md cursor-pointer
        "
      >
        <ChevronLeft className="w-5 h-5" />
      </button>

      {/* Page Numbers */}
      <div className="flex items-center gap-2">
        {pageNumbers.map((page, idx) =>
          page === '...' ? (
            <span
              key={`ellipsis-${idx}`}
              className="px-1 text-gray-300 font-bold self-end pb-1"
            >
              ...
            </span>
          ) : (
            <button
              key={page}
              onClick={() => handlePageChange(page as number)}
              disabled={isLoading}
              className={`
                min-w-[42px] h-[42px] px-2 rounded-xl font-bold transition-all duration-300
                ${
                  currentPage === page
                    ? 'bg-[#bbf7d0] text-green-900 shadow-sm scale-110 border border-green-200'
                    : 'bg-white border border-gray-100 text-gray-500 hover:bg-green-50 hover:text-green-600 hover:border-green-100'
                }
                disabled:opacity-30 disabled:cursor-not-allowed cursor-pointer
              `}
            >
              {page}
            </button>
          )
        )}
      </div>

      {/* Next */}
      <button
        onClick={() => handlePageChange(currentPage + 1)}
        disabled={currentPage === totalPages || isLoading}
        aria-label="Next page"
        className="
          p-2.5 rounded-xl border border-gray-100 bg-white
          text-gray-600 hover:bg-green-50 hover:text-green-600 hover:border-green-200
          disabled:opacity-30 disabled:cursor-not-allowed
          transition-all duration-300 shadow-sm hover:shadow-md cursor-pointer
        "
      >
        <ChevronRight className="w-5 h-5" />
      </button>
    </div>
  );
};

export default Pagination;

import { useNavigate } from 'react-router-dom';
// Force re-index
import { getOptimizedImage } from '../../utils/imageUtils';
import type { RootState } from '../../redux/store';
import React from 'react';
import { motion } from 'framer-motion';
// import { categoryBgVariants } from '../../utils/colors';
import { useAppDispatch } from '../../redux/actions/useDispatch';
import {
  addToCart,
  updateCartQty,
  removeCartItem,
} from '../../redux/actions/cartActions';
import {
  addToWishlist,
  removeWishlistItem,
} from '../../redux/actions/wishlistActions';
import { useSelector } from 'react-redux';
import { Plus, Minus, Heart } from 'lucide-react';
import toast from 'react-hot-toast';

import type { CustomerProductCardProps } from '../../types/Product';

interface Props extends CustomerProductCardProps {
  index?: number;
}

export default function ProductCard({
  _id,
  name,
  image,
  price,
  stock,
  index = 0,
}: Props) {
  const navigate = useNavigate();
  const dispatch = useAppDispatch();
  const { itemMap } = useSelector((state: RootState) => state.cart);
  const { idMap: wishlistIdMap } = useSelector(
    (state: RootState) => state.wishlist
  );
  const { user } = useSelector((state: RootState) => state.auth);

  const cartItem = itemMap[_id];
  const quantity = cartItem ? cartItem.quantity : 0;

  const wishlistId = wishlistIdMap[_id];
  const isInWishlist = !!wishlistId;

  const handleWishlistFn = async (e: React.MouseEvent) => {
    e.stopPropagation();
    if (!user) {
      toast.error('Please login to use wishlist');
      return;
    }

    if (isInWishlist) {
      await dispatch(removeWishlistItem(wishlistId));
      toast.success('Removed from wishlist');
    } else {
      await dispatch(
        addToWishlist(_id, {
          _id,
          name,
          price,
          images: [image],
          stock: stock || 0,
          description: '', // Optional or empty for now
        })
      );
      toast.success('Added to wishlist');
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.4, ease: 'easeOut' },
    },
  } as any;

  return (
    <motion.div
      variants={itemVariants}
      whileHover={{ y: -5 }}
      onClick={() => navigate(`/products/${_id}`)}
      className="bg-white rounded-2xl p-4 shadow-md hover:shadow-xl transition-all cursor-pointer group h-full flex flex-col"
    >
      <div
        className={`relative bg-gray-50 rounded-xl p-4 mb-3 overflow-hidden h-40 flex items-center justify-center`}
      >
        <img
          src={getOptimizedImage(image, 400)} // Request 400px for card
          alt={name}
          loading={index > 4 ? 'lazy' : 'eager'}
          className="w-full h-full object-contain group-hover:scale-110 transition-transform mix-blend-multiply relative z-10"
        />
        {/* Interior placeholder to bridge gap before image arrives */}
        <div className="absolute inset-0 bg-gray-100 animate-pulse rounded-xl" />
        {stock !== undefined && stock < 10 && stock > 0 && (
          <span className="absolute top-2 right-2 bg-amber-100 text-amber-700 text-[10px] px-2 py-0.5 rounded-full font-bold uppercase tracking-wider z-20">
            Low
          </span>
        )}
        {stock === 0 && (
          <span className="absolute top-2 right-2 bg-red-100 text-red-700 text-[10px] px-2 py-0.5 rounded-full font-bold uppercase tracking-wider z-20">
            Sold Out
          </span>
        )}
        <button
          onClick={handleWishlistFn}
          className={`absolute top-2 left-2 p-1.5 rounded-full transition-colors cursor-pointer z-20 ${
            isInWishlist
              ? 'bg-red-50 text-red-500 hover:bg-red-100'
              : 'bg-white/50 hover:bg-white text-gray-400 hover:text-red-500'
          }`}
        >
          <Heart size={16} fill={isInWishlist ? 'currentColor' : 'none'} />
        </button>
      </div>
      <div className="flex flex-col flex-grow text-left">
        <p className="text-gray-900 font-bold text-sm mb-1 line-clamp-2 leading-tight">
          {name}
        </p>
        <div className="mt-auto pt-2 flex items-center justify-between gap-2">
          <p className="text-green-600 font-bold text-base">{price}</p>
          {quantity === 0 ? (
            <button
              onClick={(e) => {
                e.stopPropagation();
                if ((stock ?? 1) > 0) {
                  dispatch(
                    addToCart(_id, 1, {
                      _id,
                      name,
                      price,
                      images: [image],
                      stock: stock ?? 0,
                    })
                  );
                }
              }}
              disabled={stock === 0}
              className={`px-3 sm:px-4 py-1.5 rounded-lg border font-bold text-[10px] sm:text-xs uppercase transition-all whitespace-nowrap flex items-center justify-center ${
                stock === 0
                  ? 'border-gray-200 text-gray-400 cursor-not-allowed bg-gray-50'
                  : 'border-green-600 text-green-600 hover:bg-green-50 cursor-pointer min-w-[80px]'
              }`}
            >
              {stock === 0 ? 'SOLD OUT' : 'ADD'}
            </button>
          ) : (
            <div
              className="flex items-center gap-2 bg-green-50 rounded-lg px-2 py-1 border border-green-200"
              onClick={(e) => e.stopPropagation()}
            >
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  if (quantity > 0 && cartItem) {
                    if (quantity === 1) {
                      dispatch(removeCartItem(cartItem._id));
                    } else {
                      dispatch(updateCartQty(cartItem._id, quantity - 1));
                    }
                  }
                }}
                className="w-6 h-6 flex items-center justify-center bg-white rounded text-green-700 shadow-sm hover:bg-gray-50 cursor-pointer"
              >
                <Minus size={14} strokeWidth={3} />
              </button>
              <span className="text-sm font-bold text-green-700 min-w-[20px] text-center">
                {quantity}
              </span>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  if (stock !== undefined && quantity < stock) {
                    if (cartItem) {
                      dispatch(updateCartQty(cartItem._id, quantity + 1));
                    }
                  } else if (stock !== undefined && quantity >= stock) {
                    toast.error('Maximum available stock reached');
                  }
                }}
                disabled={stock !== undefined && quantity >= stock}
                className={`w-6 h-6 flex items-center justify-center rounded text-white shadow-sm transition-colors ${
                  stock !== undefined && quantity >= stock
                    ? 'bg-gray-300 cursor-not-allowed'
                    : 'bg-green-700 hover:bg-green-800 cursor-pointer'
                }`}
              >
                <Plus size={14} strokeWidth={3} />
              </button>
            </div>
          )}
        </div>
      </div>
    </motion.div>
  );
}

import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useAppDispatch } from '../../redux/actions/useDispatch';
import { fetchProducts } from '../../redux/actions/productActions';
import {
  selectProducts,
  selectProductLoading,
} from '../../redux/selectors/productSelectors';
import ProductCard from './ProductCard';
import Button from '../common/Button';
import EmptyState from '../common/EmptyState';
import Skeleton from '../common/Skeleton';
import { ArrowRight, PackageX } from 'lucide-react';

export default function ProductsSection() {
  const dispatch = useAppDispatch();
  const navigate = useNavigate();
  const products = useSelector(selectProducts);
  const loading = useSelector(selectProductLoading);

  useEffect(() => {
    if (products.length === 0) {
      dispatch(fetchProducts());
    }
  }, [dispatch, products.length]);

  // Show only first 8 products
  const displayProducts = products
    .filter((p: any) => p.isActive !== false)
    .slice(0, 8);

  // Initial loading state (only if no products)
  if (loading && products.length === 0) {
    return (
      <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-12 py-16">
        <div className="flex items-center justify-between mb-8">
          <Skeleton variant="rect" width={200} height={40} />
          <Skeleton variant="rect" width={100} height={32} />
        </div>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-4 gap-4 sm:gap-6">
          {[...Array(4)].map((_, i) => (
            <div
              key={i}
              className="bg-white rounded-2xl p-4 border border-gray-100 flex flex-col h-80"
            >
              <Skeleton width="100%" height={160} className="mb-4 rounded-xl" />
              <Skeleton variant="text" width="80%" />
              <div className="mt-auto flex justify-between">
                <Skeleton width={60} height={24} />
                <Skeleton width={80} height={32} />
              </div>
            </div>
          ))}
        </div>
      </section>
    );
  }

  return (
    <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-12 py-16">
      <div className="flex items-center justify-between mb-8">
        <div>
          <h2 className="text-3xl md:text-4xl font-bold text-gray-900">
            Our Products
          </h2>
          <p className="text-gray-500 mt-1">Fresh selections for you</p>
        </div>
        <Button
          onClick={() => navigate('/products')}
          variant="outline"
          rightIcon={<ArrowRight className="w-4 h-4" />}
        >
          View All
        </Button>
      </div>

      {products.length === 0 ? (
        <EmptyState
          title="No Products Found"
          description="We couldn't find any products at the moment. Please check back later."
          icon={<PackageX className="w-12 h-12 text-gray-400" />}
        />
      ) : (
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-4 gap-4 sm:gap-6">
          {displayProducts.map((product, index) => (
            <ProductCard
              key={product._id}
              _id={product._id}
              name={product.name}
              price={product.price}
              image={product.images[0] || '/img/placeholder.png'}
              stock={product.stock}
              index={index}
            />
          ))}
        </div>
      )}
    </section>
  );
}

import React, { useState, useRef, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import {
  LogOut,
  User,
  ShoppingBag,
  ChevronDown,
  ShieldCheck,
  Briefcase,
} from 'lucide-react';
import { logout } from '../../redux/actions/authActions';
import type { RootState } from '../../redux/rootReducer';

const UserProfileDropdown: React.FC = () => {
  const dispatch = useDispatch<any>();
  const navigate = useNavigate();
  const { user } = useSelector((state: RootState) => state.auth);
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLogout = async () => {
    await dispatch(logout());
    navigate('/login');
    setIsOpen(false);
  };

  if (!user) {
    return null;
  }

  // Get initials or first letter of name/email
  // Get initials or first letter of name/email
  const displayName = user.name || user.email?.split('@')[0] || 'User';
  const initial = displayName.charAt(0).toUpperCase();

  return (
    <div className="relative" ref={dropdownRef}>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="flex items-center gap-2 hover:bg-gray-100 p-1.5 rounded-lg transition-colors focus:outline-none"
      >
        <div className="w-8 h-8 rounded-full bg-green-100 flex items-center justify-center text-green-700 font-bold border border-green-200">
