import type { Order } from '../../../redux/types/orderTypes';
import { statusChip, CARD_BG } from './OrderConstant';
import { Link } from 'react-router-dom';

import { useSelector } from 'react-redux';
import type { RootState } from '../../../redux/store';

export default function OrderCard({ order }: { order: Order }) {
  // Access global products to fix missing population
  const allProducts = useSelector((state: RootState) => state.product.products);

  // Helper to get product details (handles unpopulated IDs)
  const getProduct = (item: any) => {
    const id =
      typeof item.productId === 'string' ? item.productId : item.productId?._id;
    return allProducts.find((p) => p._id === id) || item.productId || {};
  };

  return (
    <Link
      to={`/orders/${order._id}`}
      className={`
        block rounded-3xl p-6 border border-gray-200/50
        shadow-[0_4px_20px_rgba(0,0,0,0.06)] 
        hover:shadow-[0_8px_30px_rgba(0,0,0,0.10)]
        hover:-translate-y-1 transition-all duration-300 
        animate-slideUp ${CARD_BG} cursor-pointer group relative overflow-hidden
      `}
    >
      {/* GrocEazy Brand Header */}
      <div className="flex items-center gap-2 mb-4 pb-4 border-b border-gray-100">
        <div className="w-8 h-8 rounded-lg bg-primary/10 flex items-center justify-center">
          <span className="font-bold text-primary text-xl">G</span>
        </div>
        <div>
          <h3 className="font-bold text-gray-900 leading-none">GrocEazy</h3>
          <span className="text-xs text-gray-500">Order #{order._id}</span>
        </div>
        <div className="ml-auto">
          {statusChip[order.status] || statusChip['Pending']}
        </div>
      </div>

      {/* Items List */}
      <div className="space-y-3 mb-6">
        {order.items.slice(0, 4).map((item, idx) => {
          const product = getProduct(item);
          const name = product.name || 'Unknown Item';
          return (
            <div
              key={idx}
              className="flex items-center text-sm group-hover:bg-gray-50/50 p-1 rounded transition-colors"
            >
              <span className="font-medium text-gray-700 truncate max-w-[200px]">
                {name}
              </span>
              <span className="text-gray-500 whitespace-nowrap font-medium ml-1">
                * {item.quantity}
              </span>
            </div>
          );
        })}
        {order.items.length > 4 && (
          <p className="text-xs text-gray-400 pl-1">
            + {order.items.length - 4} more items...
          </p>
        )}
      </div>

      {/* Footer: Date & Total */}
      <div className="flex justify-between items-center text-sm pt-2">
        <span className="text-gray-500">
          {new Date(order.createdAt).toLocaleDateString()}
        </span>
        <div className="text-right">
          <span className="text-xs text-gray-400 block mb-0.5">
            Total Amount
          </span>
          <span className="font-bold text-gray-900 text-lg">
            {Number(order.totalAmount).toFixed(2)}
          </span>
        </div>
      </div>
    </Link>
  );
}

import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { Plus, MapPin, Edit2, Trash2, CheckCircle, Star } from 'lucide-react';

import type { AppDispatch } from '../../../redux/store';
import type { IAddress, IUser } from '../../../redux/types/authTypes';
import { addAddress, updateAddress, deleteAddress } from '../../../redux/actions/profileActions';

import AddressModal from './AddressModal';

interface AddressManagerProps {
    user: IUser;
}

export default function AddressManager({ user }: AddressManagerProps) {
    const dispatch = useDispatch<AppDispatch>();
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingAddress, setEditingAddress] = useState<IAddress | null>(null);
    const [loadingAction, setLoadingAction] = useState<string | null>(null);

    const handleSave = async (addressData: Partial<IAddress>) => {
        if (loadingAction) return; // Prevent double submit
        setLoadingAction('save');
        try {
            if (editingAddress && editingAddress._id) {
                // Merge with existing address to ensure no fields are lost
                const fullAddress = { ...editingAddress, ...addressData };

                // Strip all immutable/metadata fields to prevent backend 500 errors
                // eslint-disable-next-line @typescript-eslint/no-unused-vars
                const { _id, __v, createdAt, updatedAt, ...cleanData } = fullAddress as any;

                await dispatch(updateAddress(editingAddress._id, cleanData));
            } else {
                await dispatch(addAddress(addressData));
            }
        } catch (error) {
            console.error('Failed to save address:', error);
        } finally {
            setLoadingAction(null);
            setIsModalOpen(false); // Close modal here only after logic runs
        }
    };

    const handleDelete = async (addressId: string) => {
        if (loadingAction) return;
        if (confirm('Are you sure you want to delete this address?')) {
            setLoadingAction(addressId);
            try {
                await dispatch(deleteAddress(addressId));
            } catch (error) {
                console.error('Failed to delete address:', error);
            } finally {
                setLoadingAction(null);
            }
        }
    };

    const handleSetDefault = async (address: IAddress) => {
        if (loadingAction || address.isDefault || !address._id) return;
        setLoadingAction(address._id);
        try {
            // Strip all immutable/metadata fields
            // eslint-disable-next-line @typescript-eslint/no-unused-vars
            const { _id, __v, createdAt, updatedAt, ...cleanData } = address as any;

            await dispatch(updateAddress(address._id, { ...cleanData, isDefault: true }));
        } catch (error) {
            console.error('Failed to set default:', error);
        } finally {
            setLoadingAction(null);
        }
    };

    const openAddModal = () => {
        setEditingAddress(null);
        setIsModalOpen(true);
    };

    const openEditModal = (address: IAddress) => {
        setEditingAddress(address);
        setIsModalOpen(true);
    };

    return (
        <div className="space-y-6">
            <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">Saved Addresses</h3>
                <button
                    onClick={openAddModal}
                    className="flex items-center gap-2 px-4 py-2 bg-green-50 text-green-700 rounded-lg hover:bg-green-100 transition-colors font-medium text-sm"
                >
                    <Plus size={16} />
                    Add New Address
                </button>
            </div>

            {!user.addresses || user.addresses.length === 0 ? (
                <div className="text-center py-12 bg-gray-50 rounded-xl border border-dashed border-gray-200">
                    <MapPin size={48} className="mx-auto text-gray-300 mb-3" />
                    <p className="text-gray-500">No addresses saved yet.</p>
                    <button
                        onClick={openAddModal}
                        className="mt-2 text-green-600 hover:text-green-700 font-medium text-sm"
                    >
                        Add your first address
                    </button>
                </div>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {user.addresses
                        .filter(address => address && address.street && address.city) // Filter out broken addresses
                        .map((address) => (
                            <div
                                key={address._id || Math.random()}
                                className={`relative p-5 rounded-xl border shadow-sm transition-all group ${address.isDefault
                                    ? 'border-green-500 bg-green-50/30 ring-2 ring-green-200'
                                    : 'border-gray-100 bg-white hover:border-gray-200'
                                    }`}
                            >
                                {address.isDefault && (
                                    <div className="absolute -top-3 left-4 bg-green-600 text-white flex items-center gap-1 text-xs font-bold px-3 py-1 rounded-full shadow-sm">
                                        <CheckCircle size={12} />
                                        Default Address
                                    </div>
                                )}

                                <div className="flex items-start gap-3 mb-3 mt-2">
                                    <div className={`p-2 rounded-lg ${address.isDefault ? 'bg-green-100 text-green-700' : 'bg-gray-50 text-gray-400'}`}>
                                        <MapPin size={20} />
                                    </div>
                                    <div>
                                        <p className="font-medium text-gray-900">
                                            {address.street}
                                        </p>
                                        <p className="text-sm text-gray-500">
                                            {address.city}, {address.state} {address.zipCode}
                                        </p>
                                        <p className="text-sm text-gray-400">{address.country}</p>
                                    </div>
                                </div>

                                <div className="flex items-center justify-between mt-4 pt-4 border-t border-gray-50">
                                    {!address.isDefault ? (
                                        <button
                                            onClick={() => handleSetDefault(address)}
                                            disabled={loadingAction === address._id}
                                            className="text-xs text-gray-500 hover:text-green-600 font-medium flex items-center gap-1 transition-colors"
                                        >
                                            <Star size={12} />
                                            Set as Default
                                        </button>
                                    ) : (
                                        <span className="text-xs text-green-600 font-semibold flex items-center gap-1">
                                            <Star size={12} fill="currentColor" />
                                            Active Default
                                        </span>
                                    )}
                                    <div className="flex items-center gap-2 ml-auto">
                                        <button
                                            onClick={() => openEditModal(address)}
                                            className="flex items-center gap-1 px-3 py-1.5 text-xs font-medium text-gray-600 hover:bg-gray-50 rounded-md transition-colors border border-gray-100 hover:border-gray-200"
                                        >
                                            <Edit2 size={12} />
                                            Edit
                                        </button>
                                        <button
                                            onClick={() => address._id && handleDelete(address._id)}
                                            disabled={loadingAction === address._id}
                                            className="flex items-center gap-1 px-3 py-1.5 text-xs font-medium text-red-600 hover:bg-red-50 rounded-md transition-colors border border-gray-100 hover:border-red-100"
                                        >
                                            {loadingAction === address._id ? (
                                                <span className="w-3 h-3 border border-red-200 border-t-red-600 rounded-full animate-spin"></span>
                                            ) : (
                                                <Trash2 size={12} />
                                            )}
                                            Delete
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}
                </div>
            )}

            <AddressModal
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSave={handleSave}
                initialData={editingAddress}
            />
        </div>
    );
}

import { useState, useEffect, type ChangeEvent, type FormEvent } from 'react';
import { X, Save } from 'lucide-react';
import type { IAddress } from '../../../redux/types/authTypes';

interface AddressModalProps {
    isOpen: boolean;
    onClose: () => void;
    onSave: (address: Partial<IAddress>) => void;
    initialData?: IAddress | null;
}

export default function AddressModal({
    isOpen,
    onClose,
    onSave,
    initialData,
}: AddressModalProps) {
    const [formData, setFormData] = useState<Partial<IAddress>>({
        street: '',
        city: '',
        state: '',
        zipCode: '',
        country: '',
        isDefault: false,
    });

    const [errors, setErrors] = useState<{ [key: string]: string }>({});

    useEffect(() => {
        if (initialData) {
            setFormData({
                // Preserve _id explicitly
                _id: initialData._id,
                street: initialData.street || '',
                city: initialData.city || '',
                state: initialData.state || '',
                zipCode: initialData.zipCode || '',
                country: initialData.country || '',
                isDefault: initialData.isDefault || false,
            });
        } else {
            setFormData({
                street: '',
                city: '',
                state: '',
                zipCode: '',
                country: '',
                isDefault: false,
            });
        }
        setErrors({});
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [initialData, isOpen]);

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value, type, checked } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
        // Clear error when user types
        if (errors[name]) {
            setErrors(prev => ({ ...prev, [name]: '' }));
        }
    };

    const validateForm = () => {
        const newErrors: { [key: string]: string } = {};

        if (!formData.street || formData.street.length < 5) {
            newErrors.street = 'Street address must be at least 5 characters';
        }
        if (!formData.city || formData.city.length < 2) {
            newErrors.city = 'City must be at least 2 characters';
        }
        if (!formData.state || formData.state.length < 2) {
            newErrors.state = 'State must be at least 2 characters';
        }
        if (!formData.country || formData.country.length < 2) {
            newErrors.country = 'Country must be at least 2 characters';
        }
        // Basic Zip Code validation (6 digits minimum)
        const zipRegex = /^\d{6}(-\d{6})?$/;

        if (!formData.zipCode || !zipRegex.test(formData.zipCode) || /^0+$/.test(formData.zipCode)) {
            newErrors.zipCode = 'Invalid Zip Code (e.g. 10001)';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = (e: FormEvent) => {
        e.preventDefault();
        if (validateForm()) {
            onSave(formData);
            onClose();
        }
    };

    if (!isOpen) {
        return null;
    }

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/50 backdrop-blur-sm">
            <div className="bg-white rounded-2xl w-full max-w-lg shadow-xl animate-in fade-in zoom-in-95 duration-200">
                <div className="flex items-center justify-between p-6 border-b border-gray-100">
                    <h2 className="text-xl font-bold text-gray-900">
                        {initialData ? 'Edit Address' : 'Add New Address'}
                    </h2>
                    <button
                        onClick={onClose}
                        className="p-2 hover:bg-gray-100 rounded-full transition-colors text-gray-500"
                    >
                        <X size={20} />
                    </button>
                </div>

                <form onSubmit={handleSubmit} className="p-6 space-y-4">
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                            Street Address
                        </label>
                        <input
                            type="text"
                            name="street"
                            value={formData.street}
                            onChange={handleChange}
                            required
                            placeholder="123 Main St"
                            className={`w-full px-4 py-2 rounded-lg border ${errors.street ? 'border-red-500 focus:ring-red-200' : 'border-gray-200 focus:ring-green-500/20 focus:border-green-500'} focus:outline-none focus:ring-2 transition-all`}
                        />
                        {errors.street && <p className="text-xs text-red-500 mt-1">{errors.street}</p>}
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                City
                            </label>
                            <input
                                type="text"
                                name="city"
                                value={formData.city}
                                onChange={handleChange}
                                required
                                placeholder="New York"
                                className={`w-full px-4 py-2 rounded-lg border ${errors.city ? 'border-red-500 focus:ring-red-200' : 'border-gray-200 focus:ring-green-500/20 focus:border-green-500'} focus:outline-none focus:ring-2 transition-all`}
                            />
                            {errors.city && <p className="text-xs text-red-500 mt-1">{errors.city}</p>}
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                State
                            </label>
                            <input
                                type="text"
                                name="state"
                                value={formData.state}
                                onChange={handleChange}
                                required
                                placeholder="NY"
                                className={`w-full px-4 py-2 rounded-lg border ${errors.state ? 'border-red-500 focus:ring-red-200' : 'border-gray-200 focus:ring-green-500/20 focus:border-green-500'} focus:outline-none focus:ring-2 transition-all`}
                            />
                            {errors.state && <p className="text-xs text-red-500 mt-1">{errors.state}</p>}
                        </div>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                ZIP Code
                            </label>
                            <input
                                type="text"
                                name="zipCode"
                                value={formData.zipCode}
                                onChange={handleChange}
                                required
                                placeholder="10001"
                                className={`w-full px-4 py-2 rounded-lg border ${errors.zipCode ? 'border-red-500 focus:ring-red-200' : 'border-gray-200 focus:ring-green-500/20 focus:border-green-500'} focus:outline-none focus:ring-2 transition-all`}
                            />
                            {errors.zipCode && <p className="text-xs text-red-500 mt-1">{errors.zipCode}</p>}
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Country
                            </label>
                            <input
                                type="text"
                                name="country"
                                value={formData.country}
                                onChange={handleChange}
                                required
                                placeholder="USA"
                                className={`w-full px-4 py-2 rounded-lg border ${errors.country ? 'border-red-500 focus:ring-red-200' : 'border-gray-200 focus:ring-green-500/20 focus:border-green-500'} focus:outline-none focus:ring-2 transition-all`}
                            />
                            {errors.country && <p className="text-xs text-red-500 mt-1">{errors.country}</p>}
                        </div>
                    </div>

                    <div className="flex items-center gap-2 pt-2">
                        <input
                            type="checkbox"
                            name="isDefault"
                            id="isDefault"
                            checked={formData.isDefault}
                            onChange={handleChange}
                            className="w-4 h-4 text-green-600 border-gray-300 rounded focus:ring-green-500"
                        />
                        <label htmlFor="isDefault" className="text-sm text-gray-700">
                            Set as default address
                        </label>
                    </div>

                    <div className="flex justify-end pt-4 gap-3">
                        <button
                            type="button"
                            onClick={onClose}
                            className="px-4 py-2 text-gray-700 hover:bg-gray-100 rounded-lg transition-colors font-medium"
                        >
                            Cancel
                        </button>
                        <button
                            type="submit"
                            className="flex items-center gap-2 px-6 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition-colors font-medium shadow-sm"
                        >
                            <Save size={18} />
                            Save Address
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { User, Phone, Save } from 'lucide-react';

import type { AppDispatch } from '../../../redux/store';
import type { IUser } from '../../../redux/types/authTypes';
import { updateProfile } from '../../../redux/actions/profileActions';

interface ProfileInfoProps {
    user: IUser;
}

export default function ProfileInfo({ user }: ProfileInfoProps) {
    const dispatch = useDispatch<AppDispatch>();
    const [formData, setFormData] = useState({
        name: user.name || '',
        phone: user.phone || '',
    });
    const [isSaving, setIsSaving] = useState(false);
    const [message, setMessage] = useState<{
        type: 'success' | 'error';
        text: string;
    } | null>(null);
    const [phoneError, setPhoneError] = useState('');

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));

        if (name === 'phone') {
            if (!validatePhone(value)) {
                setPhoneError('Please enter a valid phone number (at least 10 digits).');
            } else {
                setPhoneError('');
            }
        }
    };

    const validatePhone = (phone: string) => {
        // Remove spaces, dashes, parentheses to count digits
        const digits = phone.replace(/\D/g, '');
        // Basic check: at least 10 digits
        if (digits.length < 10) {
            return false;
        }
        // Check for invalid characters (allow digits, space, +, -, (, ))
        const validChars = /^[0-9+\-\s()]*$/;
        return validChars.test(phone);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        if (phoneError) {
            return;
        }

        setIsSaving(true);
        setMessage(null);

        try {
            await dispatch(updateProfile(formData));
            setMessage({ type: 'success', text: 'Profile updated successfully!' });
        } catch (error) {
            setMessage({ type: 'error', text: 'Failed to update profile.' });
        } finally {
            setIsSaving(false);
        }
    };

    return (
        <div className="max-w-xl">
            <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                        Full Name
                    </label>
                    <div className="relative">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                            <User size={18} className="text-gray-400" />
                        </div>
                        <input
                            type="text"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            className="block w-full pl-10 pr-3 py-2.5 border border-gray-200 rounded-lg focus:ring-2 focus:ring-green-500/20 focus:border-green-500 transition-all text-sm"
                            placeholder="Your full name"
                        />
                    </div>
                </div>

                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                        Phone Number
                    </label>
                    <div className="relative">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                            <Phone size={18} className="text-gray-400" />
                        </div>
                        <input
                            type="tel"
                            name="phone"
                            value={formData.phone}
                            onChange={handleChange}
                            className="block w-full pl-10 pr-3 py-2.5 border border-gray-200 rounded-lg focus:ring-2 focus:ring-green-500/20 focus:border-green-500 transition-all text-sm"
                            placeholder="+1 234 567 8900"
                        />
                    </div>
                    {phoneError && <p className="text-red-500 text-xs mt-1">{phoneError}</p>}
                </div>

                {message && (
                    <div
                        className={`p-3 rounded-lg text-sm ${message.type === 'success'
                            ? 'bg-green-50 text-green-700'
                            : 'bg-red-50 text-red-700'
                            }`}
                    >
                        {message.text}
                    </div>
                )}

                <div className="pt-2">
                    <button
                        type="submit"
                        disabled={isSaving || !!phoneError}
                        className="flex items-center gap-2 px-6 py-2.5 bg-green-600 hover:bg-green-700 text-white rounded-lg transition-colors font-medium shadow-sm disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        {isSaving ? (
                            <span className="w-5 h-5 border-2 border-white/20 border-t-white rounded-full animate-spin" />
                        ) : (
                            <Save size={18} />
                        )}
                        Save Changes
                    </button>
                </div>
            </form>
        </div>
    );
}

import { useSelector } from 'react-redux';
import { selectCartItems } from '../../../redux/selectors/cartSelectors';
import WishlistCard from './WishlistCard';

type Props = {
  items: any[];
};

export default function WishlistGrid({ items }: Props) {
  const cartItems = useSelector(selectCartItems);

  return (
    <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-8">
      {items.map((item) => {
        const isInCart = cartItems.some(
          (cartItem) => cartItem.product?._id === item.product?._id
        );
        return <WishlistCard key={item._id} item={item} isInCart={isInCart} />;
      })}
    </div>
  );
}

import OrderStatusSelect from './OrderStatusSelect';
