import { useEffect, useState } from 'react';
import type { Category } from '../../types/Category';
import api from '../../services/api';
import toast from 'react-hot-toast';

interface MobileCategorySidebarProps {
  selectedCategory: string | null;
  onSelectCategory: (id: string | null) => void;
}

export default function MobileCategorySidebar({
  selectedCategory,
  onSelectCategory,
}: MobileCategorySidebarProps) {
  const [categories, setCategories] = useState<Category[]>([]);

  useEffect(() => {
    let isMounted = true;

    api
      .get('/api/categories')
      .then((res) => {
        if (!isMounted) {
          return;
        }

        const categoriesData = (
          Array.isArray(res.data) ? res.data : res.data.categories || []
        )
          .filter((cat: Category) => cat.isActive !== false)
          .sort((a: Category, b: Category) => a.name.localeCompare(b.name));

        setCategories(categoriesData);
      })
      .catch(() => {
        if (!isMounted) {
          return;
        }
        toast.error('Failed to fetch categories');
        setCategories([]);
      });

    return () => {
      isMounted = false;
    };
  }, []);

  return (
    <div className="flex gap-4 px-4 py-2 min-w-full">
      <button
        onClick={() => onSelectCategory(null)}
        className={`flex-shrink-0 flex flex-col items-center justify-center p-2 rounded-xl transition-all w-20 border-2 cursor-pointer ${
          selectedCategory === null
            ? 'bg-green-100 text-green-700 border-green-600 scale-105'
            : 'bg-gray-50 text-gray-500 border-transparent'
        }`}
      >
        <div className="text-2xl mb-1"></div>
        <span className="text-xs font-medium truncate w-full text-center">
          All
        </span>
      </button>

      {categories.map((category) => (
        <button
          key={category._id}
          onClick={() => onSelectCategory(category._id)}
          className={`flex-shrink-0 flex flex-col items-center justify-center p-2 rounded-xl transition-all w-20 border-2 cursor-pointer ${
            selectedCategory === category._id
              ? 'bg-green-100 text-green-700 border-green-600 scale-105'
              : 'bg-gray-50 text-gray-500 border-transparent'
          }`}
        >
          <div className="w-10 h-10 rounded-full bg-white p-0.5 overflow-hidden mb-1 shadow-sm">
            {category.image ? (
              <img
                src={category.image}
                alt={category.name}
                className="w-full h-full object-cover rounded-full"
              />
            ) : (
              <span className="flex items-center justify-center h-full text-lg">
                
              </span>
            )}
          </div>
          <span className="text-xs font-medium truncate w-full text-center">
            {category.name}
          </span>
        </button>
      ))}
    </div>
  );
}

import type { FC } from 'react';
import { useEffect } from 'react';
import { useSelector } from 'react-redux';
import { fetchCategories } from '../../redux/actions/categoryActions';
import { useAppDispatch } from '../../redux/actions/useDispatch';
import type { Category } from '../../types/Category';
import PriceRangeSlider from '../common/PriceRangeSlider';
import FilterSelect from '../common/FilterSelect';
import type { RootState } from '../../redux/store';

export interface FilterState {
  selectedCategory: string | null;
  searchQuery: string;
  priceRange: [number, number];
  sortBy: string;
  stockStatus?: string;
}

interface Props {
  filters: FilterState;
  setFilters: (updates: FilterState) => void;
}

const ProductFilters: FC<Props> = ({ filters, setFilters }) => {
  const dispatch = useAppDispatch();
  const { categories, loading } = useSelector(
    (state: RootState) => state.category
  );

  useEffect(() => {
    dispatch(fetchCategories());
  }, [dispatch]);

  const handleCategoryChange = (categoryId: string) => {
    setFilters({
      ...filters,
      selectedCategory:
        filters.selectedCategory === categoryId ? null : categoryId,
    });
  };

  const visibleCategories = [...(categories || [])]
    .filter((cat: Category) => !cat.isDeleted)
    .sort((a, b) => a.name.localeCompare(b.name));

  return (
    <div className="w-full bg-white rounded-xl shadow-sm border border-gray-100 p-6 h-fit sticky top-20">
      <h3 className="font-bold text-gray-800 mb-6 text-lg">Filters</h3>

      <div className="mb-8">
        <h4 className="font-semibold text-gray-700 mb-4 text-sm uppercase tracking-wide">
          Categories
        </h4>
        {loading ? (
          <div className="space-y-2">
            {[1, 2, 3, 4].map((i) => (
              <div key={i} className="h-8 bg-gray-100 rounded animate-pulse" />
            ))}
          </div>
        ) : (
          <ul className="space-y-1">
            <li
              className={`cursor-pointer text-sm py-2 px-3 rounded-lg transition-all duration-200 ${
                !filters.selectedCategory
                  ? 'bg-green-50 text-green-700 font-semibold shadow-sm'
                  : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
              }`}
              onClick={() =>
                setFilters({
                  ...filters,
                  selectedCategory: null,
                })
              }
            >
              All Categories
            </li>

            {visibleCategories.map((category: Category) => (
              <li
                key={category._id}
                className={`cursor-pointer text-sm py-2 px-3 rounded-lg transition-all duration-200 ${
                  filters.selectedCategory === category._id
                    ? 'bg-green-50 text-green-700 font-semibold shadow-sm'
                    : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                }`}
                onClick={() => handleCategoryChange(category._id)}
              >
                {category.name}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="mb-8">
        <h4 className="font-semibold text-gray-700 mb-3 text-sm uppercase tracking-wide">
          Price Range
        </h4>
        <PriceRangeSlider
          min={0}
          max={1000}
          value={filters.priceRange}
          onChange={(newRange) =>
            setFilters({ ...filters, priceRange: newRange })
          }
        />
      </div>

      <div>
        <FilterSelect
          label="Sort By"
          value={filters.sortBy}
          onChange={(val: string) => setFilters({ ...filters, sortBy: val })}
          options={[
            { value: 'featured', label: 'Featured' },
            { value: 'price_asc', label: 'Price: Low to High' },
            { value: 'price_desc', label: 'Price: High to Low' },
            { value: 'newest', label: 'Newest Arrivals' },
          ]}
        />
      </div>
    </div>
  );
};

export default ProductFilters;

import type { FC, ChangeEvent, FormEvent } from 'react';
import { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { Trash2, Upload } from 'lucide-react';
import toast from 'react-hot-toast';

import { useAppDispatch } from '../../redux/actions/useDispatch';
import {
  createProduct,
  updateProduct as updateProductAction,
} from '../../redux/actions/productActions';
import { fetchCategories } from '../../redux/actions/categoryActions';
import { selectCategories } from '../../redux/selectors/categorySelectors';

import type { Product, ProductFormData } from '../../types/Product';

import Modal from '../common/Modal';
import Input from '../common/Input';
import Button from '../common/Button';

interface Props {
  product?: Product | null;
  onClose: () => void;
}

const ProductForm: FC<Props> = ({ product, onClose }) => {
  const dispatch = useAppDispatch();
  const categories = useSelector(selectCategories);

  // Fetch categories on mount
  useEffect(() => {
    dispatch(fetchCategories());
  }, [dispatch]);

  const [formData, setFormData] = useState<ProductFormData>({
    name: product?.name || '',
    description: product?.description || '',
    size: product?.size,
    dietary: product?.dietary,
    stock: product?.stock || 0,
    lowStockThreshold: product?.lowStockThreshold || 5,
    price: product?.price || 0,
    isActive: product?.isActive ?? true,
    categoryId:
      typeof product?.categoryId === 'object' && product?.categoryId !== null
        ? (product.categoryId as any)._id
        : product?.categoryId || '',
    images: product?.images || [],
  });

  const [imageFiles, setImageFiles] = useState<File[]>([]);
  const [imagePreviews, setImagePreviews] = useState<string[]>(
    product?.images || []
  );

  const handleChange = (
    e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => {
    const { name, value, type } = e.target;

    setFormData((prev) => ({
      ...prev,
      [name]:
        type === 'number'
          ? value === ''
            ? ''
            : Number(value)
          : type === 'checkbox'
            ? (e.target as HTMLInputElement).checked
            : value,
    }));
  };

  const handleImageFilesChange = (e: ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    if (files.length > 0) {
      files.forEach((file) => {
        const reader = new FileReader();
        reader.onloadend = () => {
          setImagePreviews((prev) => [...prev, reader.result as string]);
        };
        reader.readAsDataURL(file);
      });
      setImageFiles((prev) => [...prev, ...files]);
    }
  };

  const removeImagePreview = (index: number) => {
    setImagePreviews((prev) => prev.filter((_, i) => i !== index));
    setImageFiles((prev) => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();

    try {
      const submitData: ProductFormData = {
        ...formData,
        images: imageFiles.length > 0 ? imageFiles : formData.images,
      };

      if (product) {
        await dispatch(updateProductAction(product._id, submitData));
        toast.success('Product updated successfully');
      } else {
        await dispatch(createProduct(submitData));
        toast.success('Product created successfully');
      }

      onClose();
    } catch {
      toast.error('Failed to save product');
    }
  };

  return (
    <Modal
      isOpen
      onClose={onClose}
      title={product ? 'Edit Product' : 'Add Product'}
      maxWidth="2xl"
    >
      <form onSubmit={handleSubmit} className="space-y-6 px-1">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <Input
            label="Product Name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />

          <Input
            label="Price ()"
            type="number"
            name="price"
            value={formData.price ?? ''}
            placeholder="Enter price"
            onChange={handleChange}
            min="0"
            step="0.01"
            required
          />

          <Input
            label="Stock"
            type="number"
            name="stock"
            value={formData.stock}
            onChange={handleChange}
            min="0"
            required
          />

          <Input
            label="Low Stock Threshold"
            type="number"
            name="lowStockThreshold"
            value={formData.lowStockThreshold}
            onChange={handleChange}
            min="0"
          />

          <Input
            label="Size (optional)"
            name="size"
            value={formData.size || ''}
            onChange={handleChange}
            placeholder="e.g., 500g, 1kg, 1L"
          />

          <Input
            label="Dietary Info (optional)"
            name="dietary"
            value={formData.dietary || ''}
            onChange={handleChange}
            placeholder="e.g., Vegan, Gluten-Free"
          />

          {/* Category Dropdown */}
          <div className="md:col-span-2">
            <label className="block text-sm font-medium text-gray-700 mb-1.5">
              Category
            </label>
            <select
              name="categoryId"
              value={formData.categoryId}
              onChange={handleChange}
              className="w-full px-4 py-2.5 border-2 border-gray-200 rounded-xl focus:border-green-500 focus:ring-4 focus:ring-green-50 bg-white"
              required
            >
              <option value="">Select category</option>
              {categories
                .filter((cat) => !cat.isDeleted)
                .map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.name}
                  </option>
                ))}
            </select>
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1.5">
            Description
          </label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows={4}
            className="w-full px-4 py-2 border-2 border-gray-200 rounded-xl focus:border-green-500 focus:ring-4 focus:ring-green-50"
            required
          />
        </div>

        {/* Active Status */}
        <div className="flex items-center gap-3">
          <input
            type="checkbox"
            id="isActive"
            name="isActive"
            checked={formData.isActive}
            onChange={handleChange}
            className="w-5 h-5 text-green-600 border-gray-300 rounded focus:ring-green-500"
          />
          <label
            htmlFor="isActive"
            className="text-sm font-medium text-gray-700"
          >
            Product is active (visible to customers)
          </label>
        </div>

        {/* Images */}
        <div className="space-y-3">
          <label className="block text-sm font-medium text-gray-700">
            Product Images
          </label>

          {/* Image Previews */}
          {imagePreviews.length > 0 && (
            <div className="grid grid-cols-4 gap-4">
              {imagePreviews.map((preview, idx) => (
                <div key={idx} className="relative group">
                  <img
                    src={preview}
                    alt={`Preview ${idx + 1}`}
                    className="w-full h-24 object-cover rounded-lg border-2 border-gray-200"
                  />
                  <button
                    type="button"
                    onClick={() => removeImagePreview(idx)}
                    className="absolute top-1 right-1 p-1 bg-red-500 text-white rounded-full opacity-0 group-hover:opacity-100 transition-opacity"
                  >
                    <Trash2 className="w-3 h-3" />
                  </button>
                </div>
              ))}
            </div>
          )}

          {/* Upload Button */}
          <label className="cursor-pointer">
            <div className="flex items-center justify-center gap-2 px-4 py-3 border-2 border-dashed border-gray-300 rounded-xl hover:border-green-500 transition-colors">
              <Upload className="w-5 h-5 text-gray-400" />
              <span className="text-sm text-gray-600">
                Upload product images (multiple files)
              </span>
            </div>
            <input
              type="file"
              accept="image/*"
              multiple
              onChange={handleImageFilesChange}
              className="hidden"
            />
          </label>
        </div>

