    } catch (err: any) { // eslint-disable-line @typescript-eslint/no-explicit-any
      const errorMessage =
        err.response?.data?.message || err.message || 'Google login failed';
      dispatch({
        type: AUTH_LOGIN_FAILURE,
        payload: { error: errorMessage },
      });
    }
