import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, Link } from 'react-router-dom';
import { z } from 'zod';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { register } from '../../redux/actions/authActions';
import type { RootState } from '../../redux/rootReducer';
import GoogleAuthButton from '../../components/auth/GoogleAuthButton';
import { Eye, EyeOff, Loader2 } from 'lucide-react';
import { UserRole } from '../../constants/roles';
import toast from 'react-hot-toast';

import { passwordSchema } from '../../utils/validationSchemas';
import PasswordStrengthIndicator from '../../components/auth/PasswordStrengthIndicator';
import Input from '../../components/common/Input';
import AuthCarousel from '../../components/auth/AuthCarousel';

// Zod Schema
const registerSchema = z.object({
    name: z.string().min(1, 'Name is required'),
    email: z.string().email('Invalid email address'),
    password: passwordSchema,
    confirmPassword: z.string(),
}).refine((data) => data.password === data.confirmPassword, {
    message: "Passwords don't match",
    path: ['confirmPassword'],
});

type RegisterFormInputs = z.infer<typeof registerSchema>;

const Register: React.FC = () => {
    const dispatch = useDispatch<any>();
    const navigate = useNavigate();
    const { loading, error, accessToken, user } = useSelector((state: RootState) => state.auth);
    const [showPassword, setShowPassword] = useState(false);

    // Check if we successfully registered implies we should have a success state or just redirect?
    // Since Redux state doesn't explicitly have "registerSuccess" flag in typical simple impl,
    // we might want to just handle success in the action or a local useEffect if we track a "success" flag appropriately.
    // For now, let's assume we redirect manually or the user navigates.
    // Actually, let's use a local state for success message to show "Check email".
    const [successMsg, setSuccessMsg] = useState('');

    useEffect(() => {
        if (accessToken && user) {
            // Check for deactivated account
            if (user.isActive === false) {
                toast.error('Account deactivated. Contact support if you believe this is an error.');
                // We might want to logout here, but the user just logged in.
                // The Login page handles this by logging out.
                // Let's assume the action/reducer might handle it or we redirect to login?
                return;
            }

            toast.success('Welcome back! Youve been logged into your existing account.');

            if (user.hasPassword === false) {
                navigate('/set-password');
            } else if (user.role === UserRole.ADMIN) {
                navigate('/admin');
            } else if (user.role === UserRole.MANAGER) {
                navigate('/manager');
            } else {
                navigate('/');
            }
        }
    }, [accessToken, user, navigate]);

    const {
        register: formRegister,
        handleSubmit,
        watch,
        formState: { errors },
    } = useForm<RegisterFormInputs>({
        resolver: zodResolver(registerSchema),
        mode: 'onChange', // Validate on change for immediate feedback
    });

    const password = watch('password');

    const onSubmit = async (data: RegisterFormInputs) => {
        setSuccessMsg('');
        try {
            await dispatch(register({
                name: data.name,
                email: data.email,
                password: data.password
            }));
            // If no error thrown by dispatch (it awaits), we can set success
            // But dispatch returns promise that might reject?
            // My authActions implementation catches error and dispatches FAILURE, so checks state `error`.
            // Waiting for state update is tricky in same tick.
            // Better to check for changes in `error` via useEffect or use `unwrap` if using toolkit thunks.
            // Here we use legacy thunks.
            // Let's rely on `error` from store. If loading finishes and no error, it's success?
            // But we have a unified `loading` state.
            // Let's assume the action logic.
            setSuccessMsg('Registration successful! Please login.');
            setTimeout(() => navigate('/login'), 2000);
        } catch {
            // Redux handles error state
        }
    };

    return (
        <div className="flex min-h-screen bg-white">
            {/* Left Side - Carousel */}
            <div className="hidden lg:block lg:w-1/2 relative bg-gray-900">
                <AuthCarousel />
                <div className="absolute top-8 left-8 z-20">
                    <div className="flex items-center gap-2 text-white bg-black/20 backdrop-blur-md px-4 py-2 rounded-full border border-white/10">
                        <span className="font-bold text-xl tracking-tight">GrocEazy</span>
                    </div>
                </div>
            </div>

            {/* Right Side - Form */}
            <div className="w-full lg:w-1/2 flex items-center justify-center p-8 lg:p-16">
                <div className="w-full max-w-md space-y-8">
                    <div className="text-center lg:text-left">
                        <div className="lg:hidden flex justify-center mb-8">
                            <span className="text-2xl font-bold text-green-600">GrocEazy</span>
                        </div>
                        <h2 className="text-3xl font-bold text-gray-900 tracking-tight">
                            Create an account
                        </h2>
                        <p className="mt-2 text-sm text-gray-600">
                            Start your journey with us today.
                        </p>
                    </div>

                    {(error || successMsg) && (
                        <div className={`p-4 rounded-lg border ${error ? 'bg-red-50 border-red-200 text-red-700' : 'bg-green-50 border-green-200 text-green-700'
                            }`}>
                            <p className="text-sm font-medium">{error || successMsg}</p>
                        </div>
                    )}

                    <form onSubmit={handleSubmit(onSubmit)} className="space-y-5">
                        <Input
                            label="Full Name"
                            placeholder="Enter your full name"
                            error={errors.name?.message}
                            {...formRegister('name')}
                        />

                        <Input
                            label="Email"
                            type="email"
                            placeholder="Enter your email"
                            error={errors.email?.message}
                            {...formRegister('email')}
                        />

                        <div className="space-y-2">
                            <Input
                                label="Password"
                                type={showPassword ? "text" : "password"}
                                placeholder=""
                                error={errors.password?.message}
                                {...formRegister('password')}
                                rightIcon={
                                    <button
                                        type="button"
                                        onClick={() => setShowPassword(!showPassword)}
                                        className="hover:text-gray-600 focus:outline-none transition-colors"
                                    >
                                        {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                                    </button>
                                }
                            />
                            <PasswordStrengthIndicator password={password} />
                        </div>

                        <Input
                            label="Confirm Password"
                            type="password"
                            placeholder=""
                            error={errors.confirmPassword?.message}
                            {...formRegister('confirmPassword')}
                        />

                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full flex justify-center py-3 px-4 border border-transparent rounded-xl shadow-sm text-sm font-semibold text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 transform active:scale-[0.99]"
                        >
                            {loading ? (
                                <Loader2 className="animate-spin w-5 h-5" />
                            ) : (
                                'Sign Up'
                            )}
                        </button>
                    </form>

                    <div className="mt-6">
                        <div className="relative">
                            <div className="absolute inset-0 flex items-center">
                                <div className="w-full border-t border-gray-200" />
                            </div>
                            <div className="relative flex justify-center text-sm">
                                <span className="px-2 bg-white text-gray-500">Or continue with</span>
                            </div>
                        </div>

                        <div className="mt-6">
                            <GoogleAuthButton />
                        </div>
                    </div>

                    <p className="text-center text-sm text-gray-600">
                        Already have an account?{' '}
                        <Link to="/login" className="font-semibold text-green-600 hover:text-green-500 transition-colors">
                            Log in
                        </Link>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Register;

import { useEffect, useState, useMemo } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { PackageX, SearchX } from 'lucide-react';
import OrderCard from '../../components/customer/orders/OrderCard';
import { getMyOrders } from '../../redux/actions/orderActions';
import { fetchProducts } from '../../redux/actions/productActions';
import FilterSelect from '../../components/common/FilterSelect';
import { ORDER_STATUS_META } from '../../utils/orderStatus';
import Loader from '../../components/common/Loader';
import type { RootState } from '../../redux/rootReducer';
import type { OrderActionTypes } from '../../redux/types/orderTypes';
import type { ThunkDispatch } from 'redux-thunk';

export default function OrdersPage() {
  const dispatch =
    useDispatch<ThunkDispatch<RootState, any, OrderActionTypes>>();
  const { orders, pagination, loading, error } = useSelector(
    (state: RootState) => state.order
  );
  const [currentPage, setCurrentPage] = useState(1);
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    dispatch(getMyOrders(currentPage, 5, statusFilter));
    dispatch(fetchProducts());
  }, [dispatch, currentPage, statusFilter]);

  const filterOptions = useMemo(() => {
    const options = Object.keys(ORDER_STATUS_META).map((status) => ({
      value: status,
      label: status,
    }));
    return [{ value: 'all', label: 'All Statuses' }, ...options];
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-[60vh]">
        <Loader />
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col justify-center items-center min-h-[60vh] text-center">
        <PackageX className="w-16 h-16 text-gray-300 mb-4" />
        <h3 className="text-xl font-semibold text-gray-900">Oops!</h3>
        <p className="text-gray-500 mt-2">{error}</p>
        <button
          onClick={() => dispatch(getMyOrders())}
          className="mt-4 text-primary font-medium hover:underline cursor-pointer"
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-12 py-10 animate-fadeIn">
      <div className="flex flex-col md:flex-row md:items-end justify-between gap-6 mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">My Orders</h1>
          <p className="text-gray-600">Track all your orders with ease.</p>
        </div>

        <FilterSelect
          label="Filter by Status"
          value={statusFilter}
          options={filterOptions}
          onChange={(val) => {
            setStatusFilter(val);
            setCurrentPage(1);
          }}
          className="md:w-64"
        />
      </div>

      {orders.length === 0 ? (
        <div className="text-center py-20 bg-gray-50 rounded-3xl border border-dashed border-gray-300">
          <div className="p-4 bg-gray-100 rounded-full w-fit mx-auto text-gray-400 mb-3">
            {statusFilter === 'all' ? (
              <PackageX className="w-12 h-12" />
            ) : (
              <SearchX className="w-12 h-12" />
            )}
          </div>
          <p className="text-gray-500 text-lg">
            {statusFilter === 'all' ? 'No orders found.' : 'No matches found.'}
          </p>
          <p className="text-gray-400 text-sm mt-1">
            {statusFilter === 'all'
              ? 'Start shopping to see your orders here!'
              : `No orders currently match the "${statusFilter}" status.`}
          </p>
          {statusFilter !== 'all' && (
            <button
              onClick={() => setStatusFilter('all')}
              className="mt-4 text-primary font-semibold hover:underline cursor-pointer"
            >
              Clear filter
            </button>
          )}
        </div>
      ) : (
        <div className="space-y-10">
          {orders.map((order) => (
            <OrderCard key={order._id} order={order} />
          ))}

          {/* Pagination Controls */}
          {pagination && pagination.pages > 1 && (
            <div className="flex justify-center items-center gap-4 mt-8">
              <button
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
                className={`px-4 py-2 text-sm font-medium rounded-lg border transition-colors cursor-pointer ${
                  currentPage === 1
                    ? 'bg-gray-100 text-gray-400 border-gray-200 cursor-not-allowed'
                    : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50 hover:text-green-600'
                }`}
              >
                Previous
              </button>
              <span className="text-sm text-gray-600 font-medium">
                Page {currentPage} of {pagination.pages}
              </span>
              <button
                onClick={() =>
                  setCurrentPage((prev) => Math.min(prev + 1, pagination.pages))
                }
                disabled={currentPage === pagination.pages}
                className={`px-4 py-2 text-sm font-medium rounded-lg border transition-colors cursor-pointer ${
                  currentPage === pagination.pages
                    ? 'bg-gray-100 text-gray-400 border-gray-200 cursor-not-allowed'
                    : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50 hover:text-green-600'
                }`}
              >
                Next
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

import { useEffect, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import {
  ArrowLeft,
  Calendar,
  Clock,
  CheckCircle2,
  AlertCircle,
  Hash,
} from 'lucide-react';

import Loader from '../../components/common/Loader';
import { selectSupportMyTickets } from '../../redux/selectors/supportSelectors';
import { fetchMySupportTickets } from '../../redux/actions/supportActions';
import type { AppDispatch } from '../../redux/store';

export default function CustomerTicketDetails() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const dispatch = useDispatch<AppDispatch>();

  const tickets = useSelector(selectSupportMyTickets);

  useEffect(() => {
    if (!tickets || tickets.length === 0) {
      dispatch(fetchMySupportTickets());
    }
  }, [dispatch, tickets]);

  const ticket = useMemo(
    () => tickets?.find((t) => t._id === id),
    [tickets, id]
  );

  if (!ticket) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center p-8 bg-white/60 backdrop-blur-xl rounded-3xl border border-white/50 shadow-xl animate-fadeIn">
          <Loader />
          <p className="text-gray-500 mt-6 font-medium animate-pulse">
            Fetching ticket details...
          </p>
          <button
            onClick={() => navigate('/customer/tickets')}
            className="mt-8 px-6 py-2 rounded-full border border-primary/30 text-primary hover:bg-primary/5 transition-all text-sm font-semibold"
          >
            Back to My Tickets
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen py-10 px-4 sm:px-6 lg:px-12">
      <div className="max-w-4xl mx-auto space-y-6">
        {/* BACK BUTTON */}
        <button
          onClick={() => navigate('/customer/tickets')}
          className="flex items-center gap-2 text-gray-600 hover:text-primary transition-colors group mb-4"
        >
          <div className="p-2 rounded-full bg-white/50 group-hover:bg-white shadow-sm transition-all">
            <ArrowLeft size={20} />
          </div>
          <span className="font-medium">Back to My Tickets</span>
        </button>

        {/* MAIN CARD */}
        <div className="bg-white/70 backdrop-blur-xl border border-white/60 shadow-xl rounded-2xl relative overflow-hidden">
          <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-primary to-emerald-400" />

          <div className="p-6 sm:p-10">
            {/* HEADER */}
            <div className="flex flex-col md:flex-row md:items-start md:justify-between gap-6 mb-8">
              <div className="space-y-4">
                <div className="flex flex-wrap items-center gap-3">
                  <span className="px-3 py-1 rounded-full bg-primary/10 text-primary text-xs font-bold tracking-wider uppercase border border-primary/20">
                    Support Ticket
                  </span>
                  <span className="flex items-center gap-1 text-xs text-gray-400 font-mono bg-gray-50 px-2 py-1 rounded">
                    <Hash size={12} /> {ticket._id}
                  </span>
                </div>
                <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 leading-tight">
                  {ticket.subject}
                </h1>
              </div>

              <div className="flex-shrink-0">
                <StatusChip status={ticket.status} />
              </div>
            </div>

            {/* DESCRIPTION */}
            <div className="bg-white/50 rounded-xl p-6 border border-white/50 shadow-inner mb-8">
              <h3 className="text-sm font-semibold text-gray-900 mb-3 flex items-center gap-2">
                <AlertCircle size={16} className="text-primary" />
                Your Message
              </h3>
              <p className="text-gray-700 leading-relaxed whitespace-pre-wrap">
                {ticket.description}
              </p>
            </div>

            {/* INFO GRID */}
            <div className="grid grid-cols-1 gap-6">
              {/* TICKET INFO */}
              <div className="p-5 rounded-xl bg-gradient-to-br from-blue-50 to-blue-100/50 border border-blue-100">
                <h3 className="text-sm font-bold text-blue-900 mb-4 flex items-center gap-2">
                  <Calendar size={18} className="text-blue-600" />
                  Ticket Details
                </h3>
                <div className="space-y-4">
                  <div className="flex items-center gap-3 text-sm">
                    <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center text-blue-500 shadow-sm flex-shrink-0">
                      <Calendar size={16} />
                    </div>
                    <div>
                      <p className="text-[10px] uppercase text-gray-400 font-bold">
                        Created On
                      </p>
                      <p className="font-semibold text-gray-700">
                        {new Date(ticket.createdAt).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

function StatusChip({ status }: { status: string }) {
  const configs: any = {
    open: {
      label: 'Open',
      icon: <AlertCircle size={16} />,
      color: 'bg-yellow-100 text-yellow-700 border-yellow-200',
    },
    in_progress: {
      label: 'In Progress',
      icon: <Clock size={16} />,
      color: 'bg-blue-100 text-blue-700 border-blue-200',
    },
    resolved: {
      label: 'Resolved',
      icon: <CheckCircle2 size={16} />,
      color: 'bg-green-100 text-green-700 border-green-200',
    },
  };

  const config = configs[status] || configs.open;

  return (
    <span
      className={`flex items-center gap-2 px-4 py-1.5 rounded-full text-sm font-bold border shadow-sm ${config.color}`}
    >
      {config.icon}
      {config.label}
    </span>
  );
}

import { useEffect, useMemo, useState } from 'react';
import { useSelector } from 'react-redux';
import type { RootState } from '../../redux/store';

import {
  fetchAnalyticsProducts,
  deleteProduct as deleteProductAction,
} from '../../redux/actions/productActions';

import { buildAnalytics } from '../../utils/analyticsBuilder';

import AnalyticsCards from '../../components/manager/analytics/AnalyticsCards';
import ChartCard from '../../components/manager/analytics/ChartCard';
import RevenueBarChart from '../../utils/wrappers/RevenueBarChartWrapper';
import InventoryPieChart from '../../components/manager/analytics/IneventoryPieChart';
import ProductStatusPieChart from '../../utils/wrappers/ProductStatusPieChartWrapper';
import AnalyticsDrilldownModal from '../../components/manager/analytics/AnalyticsDrilldownModal';
import ProductForm from '../../components/products/ProductForm';
import Loader from '../../components/common/Loader';
import toast from 'react-hot-toast';
import { useAppDispatch } from '../../redux/actions/useDispatch';
import type { Product } from '../../types/Product';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
);

type AnalyzedProduct = Product & { image: string };

interface AnalyticsData {
  revenue: number;
  activeProducts: number;
  lowStock: number;
  outOfStock: number;
  healthyProducts: AnalyzedProduct[];
  lowStockProducts: AnalyzedProduct[];
  outOfStockProducts: AnalyzedProduct[];
  activeProductList: AnalyzedProduct[];
  inactiveProductList: AnalyzedProduct[];
  productsByMonth: Record<string, AnalyzedProduct[]>;
  revenueBar: { labels: string[]; data: number[] };
  productStatus: { active: number; inactive: number };
  inventoryHealth: { healthy: number; low: number; out: number };
}

export default function Analytics() {
  const dispatch = useAppDispatch();
  const { analyticsProducts: products, analyticsLoading: loading } =
    useSelector((state: RootState) => state.product);

  const [drilldown, setDrilldown] = useState<{
    isOpen: boolean;
    title: string;
    products: AnalyzedProduct[];
    type: 'healthy' | 'low' | 'out' | 'active' | 'inactive' | 'revenue';
  }>({
    isOpen: false,
    title: '',
    products: [],
    type: 'healthy',
  });

  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [isFormOpen, setIsFormOpen] = useState(false);

  useEffect(() => {
    dispatch(fetchAnalyticsProducts());
  }, [dispatch]);

  const analytics = useMemo(
    () => buildAnalytics(products) as unknown as AnalyticsData,
    [products]
  );

  const openDrilldown = (
    title: string,
    productsList: AnalyzedProduct[],
    type: 'healthy' | 'low' | 'out' | 'active' | 'inactive' | 'revenue'
  ) => {
    setDrilldown({
      isOpen: true,
      title,
      products: productsList,
      type,
    });
  };

  const handleEdit = (product: Product) => {
    setEditingProduct(product);
    setIsFormOpen(true);
    // Close drilldown modal if it's open
    setDrilldown((prev) => ({ ...prev, isOpen: false }));
  };

  const handleDelete = async (id: string) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      try {
        await dispatch(deleteProductAction(id));
        toast.success('Product deleted successfully');
        // Update drilldown if open to reflect deletion
        if (drilldown.isOpen) {
          setDrilldown((prev) => ({
            ...prev,
            products: prev.products.filter((p) => p._id !== id),
          }));
        }
      } catch {
        toast.error('Failed to delete product');
      }
    }
  };

  if (loading && products.length === 0) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50">
        <Loader size="lg" />
        <p className="mt-4 text-gray-500 font-medium animate-pulse">
          Analyzing your inventory...
        </p>
      </div>
    );
  }

  return (
    <div className="min-h-screen max-w-[1400px] mx-auto px-6 sm:px-12 lg:px-20 py-10 space-y-12">
      {/* HEADER */}
      <div className="animate-fadeIn">
        <h1 className="text-3xl font-extrabold text-gray-900">
          Manager Analytics
        </h1>
        <p className="text-gray-500 mt-1">
          Revenue, inventory & product insights
        </p>
      </div>

      {/* METRIC CARDS */}
      <div className="relative z-20">
        <AnalyticsCards
          analytics={analytics}
          onDrilldown={(title, productsList, type) =>
            openDrilldown(title, productsList as AnalyzedProduct[], type as any)
          }
        />
      </div>

      {/* CHARTS */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">
        <div className="cursor-pointer">
          <ChartCard
            title="Revenue Trend"
            description="Click bars to see products added that month"
          >
            <RevenueBarChart
              data={analytics.revenueBar}
              onSegmentClick={(label) =>
                openDrilldown(
                  `Products from ${label}`,
                  analytics.productsByMonth[label] || [],
                  'revenue'
                )
              }
            />
          </ChartCard>
        </div>

        <div className="cursor-pointer">
          <ChartCard
            title="Inventory Health"
            description="Click segments to view product lists"
          >
            <InventoryPieChart
              data={analytics.inventoryHealth}
              onSegmentClick={(segment) => {
                if (segment === 'Healthy') {
                  openDrilldown(
                    'Healthy Products',
                    analytics.healthyProducts,
                    'healthy'
                  );
                }
                if (segment === 'Low Stock') {
                  openDrilldown(
                    'Low Stock Products',
                    analytics.lowStockProducts,
                    'low'
                  );
                }
                if (segment === 'Out of Stock') {
                  openDrilldown(
                    'Out of Stock Products',
                    analytics.outOfStockProducts,
                    'out'
                  );
                }
              }}
            />
          </ChartCard>
        </div>

        <div className="cursor-pointer">
          <ChartCard
            title="Product Status"
            description="Click segments to view status details"
          >
            <ProductStatusPieChart
              data={analytics.productStatus}
              onSegmentClick={(segment) => {
                if (segment === 'Active Products') {
                  openDrilldown(
                    'Active Products',
                    analytics.activeProductList,
                    'active'
                  );
                }
                if (segment === 'Inactive Products') {
                  openDrilldown(
                    'Inactive Products',
                    analytics.inactiveProductList,
                    'inactive'
                  );
                }
              }}
            />
          </ChartCard>
        </div>
      </div>

      <AnalyticsDrilldownModal
        isOpen={drilldown.isOpen}
        onClose={() => setDrilldown((prev) => ({ ...prev, isOpen: false }))}
        title={drilldown.title}
        products={drilldown.products}
        type={drilldown.type}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />

      {isFormOpen && (
        <ProductForm
          key={editingProduct?._id ?? 'new'}
          product={editingProduct}
          onClose={() => setIsFormOpen(false)}
        />
      )}
    </div>
  );
}

import React, { type FC, useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { Package, AlertTriangle, CheckCircle } from 'lucide-react';

import { useAppDispatch } from '../../redux/actions/useDispatch';
import { fetchManagerProducts } from '../../redux/actions/productActions';
import { fetchCategories } from '../../redux/actions/categoryActions';
import {
  selectProducts,
  selectProductLoading,
  selectProductPagination,
} from '../../redux/selectors/productSelectors';
import { selectCategories } from '../../redux/selectors/categorySelectors';
import Pagination from '../../components/common/Pagination';
import DebouncedSearch from '../../components/common/DebouncedSearch';
import FilterSelect from '../../components/common/FilterSelect';

import Loader from '../../components/common/Loader';
import EmptyState from '../../components/common/EmptyState';

import { InventoryCharts } from './InventoryCharts';
import { InventoryAlertsModal } from './InventoryAlertsModal';
import type { Product } from '../../types/Product';

interface StockStatus {
  label: string;
  color: string;
  icon: any;
}

const InventoryRow = React.memo(
  ({
    product,
    categoryName,
    status,
  }: {
    product: Product;
    categoryName: string;
    status: StockStatus;
  }) => {
    const StatusIcon = status.icon;
    return (
      <tr
        className={`hover:bg-gray-50/50 transition-opacity ${!product.isActive ? 'opacity-60' : ''}`}
      >
        <td className="px-6 py-4">
          <div className="flex items-center space-x-4">
            <div className="h-10 w-10 rounded-lg bg-gray-100 p-1 flex-shrink-0">
              <img
                className="h-full w-full rounded-md object-cover"
                src={
                  product.images?.[0] ||
                  `https://ui-avatars.com/api/?name=${product.name}`
                }
                alt={product.name}
                loading="lazy"
                width={40}
                height={40}
              />
            </div>
            <div>
              <div className="text-sm font-semibold text-gray-900">
                {product.name}
              </div>
              {product.size && (
                <div className="text-xs text-gray-500">{product.size}</div>
              )}
            </div>
          </div>
        </td>

        <td className="px-6 py-4">
          <span className="text-sm text-gray-600">{categoryName}</span>
        </td>

        <td className="px-6 py-4">
          <span className="text-sm font-medium text-gray-900">
            {product.price.toFixed(2)}
          </span>
        </td>

        <td className="px-6 py-4">
          <span className={`text-sm font-bold ${status.color.split(' ')[0]}`}>
            {product.stock}
          </span>
        </td>

        <td className="px-6 py-4">
          <span
            className={`inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-medium ${status.color}`}
          >
            <StatusIcon size={12} />
            {status.label}
          </span>
        </td>
      </tr>
    );
  }
);

InventoryRow.displayName = 'InventoryRow';

const Inventory: FC = () => {
  const dispatch = useAppDispatch();
  const products = useSelector(selectProducts);
  const loading = useSelector(selectProductLoading);
  const categories = useSelector(selectCategories);
  const pagination = useSelector(selectProductPagination);

  const [search, setSearch] = useState('');
  const [stockFilter, setStockFilter] = useState('');
  const [isAlertsOpen, setIsAlertsOpen] = useState(false);
  const [page, setPage] = useState(1);

  // 1. Fetch Categories only once on mount
  useEffect(() => {
    dispatch(fetchCategories());
  }, [dispatch]);

  // 2. Fetch Products when page or search term changes
  // If stock filter is active, fetch more items for client-side filtering
  useEffect(() => {
    const limit = stockFilter ? 1000 : 10;
    const fetchPage = stockFilter ? 1 : page;
    dispatch(
      fetchManagerProducts(fetchPage, limit, search, undefined, stockFilter)
    );
  }, [dispatch, page, search, stockFilter]);

  // 3. Memoized Category Lookup Map for performance
  const categoryMap = React.useMemo(() => {
    const map: Record<string, string> = {};
    categories.forEach((cat) => {
      map[cat._id] = cat.name;
    });
    return map;
  }, [categories]);

  const getStockStatus = (stock: number, threshold: number = 5) => {
    if (Number(stock) === 0) {
      return {
        label: 'Out of Stock',
        color: 'text-red-600 bg-red-50',
        icon: AlertTriangle,
      };
    }
    if (stock <= threshold) {
      return {
        label: 'Low Stock',
        color: 'text-orange-600 bg-orange-50',
        icon: AlertTriangle,
      };
    }
    return {
      label: 'In Stock',
      color: 'text-green-600 bg-green-50',
      icon: CheckCircle,
    };
  };

  const getCategoryId = (p: Product) => {
    if (typeof p.categoryId === 'object' && p.categoryId !== null) {
      return (p.categoryId as { _id: string })._id;
    }
    if (typeof p.categoryId === 'string' && p.categoryId) {
      return p.categoryId;
    }
    // Fallback for different data structures if they exist
    const prodAsAny = p as any;
    if (typeof prodAsAny.category === 'object' && prodAsAny.category !== null) {
      return (prodAsAny.category as { _id: string })._id;
    }
    return prodAsAny.category;
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <InventoryAlertsModal
        isOpen={isAlertsOpen}
        onClose={() => setIsAlertsOpen(false)}
      />

      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Inventory</h1>
          <p className="text-gray-500">
            Track and monitor product stock levels
          </p>
        </div>
        <button
          onClick={() => setIsAlertsOpen(true)}
          className="flex items-center gap-2 px-4 py-2 bg-white border border-gray-200 rounded-lg text-gray-700 font-medium hover:bg-gray-50 hover:border-gray-300 transition-all shadow-sm"
        >
          <AlertTriangle size={18} className="text-orange-500" />
          Stock Alerts
        </button>
      </div>

      <InventoryCharts products={products} categories={categories} />

      <div className="bg-white rounded-2xl shadow-sm border border-gray-100 mb-6">
        <div className="p-4 border-b border-gray-100 flex flex-col sm:flex-row gap-4 items-center justify-between">
          <div className="max-w-md w-full">
            <DebouncedSearch
              placeholder="Search inventory..."
              initialValue={search}
              onSearch={(val) => {
                setSearch(val);
                setPage(1);
              }}
            />
          </div>
          <div className="flex items-center gap-3 w-full sm:w-auto">
            <FilterSelect
              label="Stock Level"
              value={stockFilter}
              options={[
                { value: '', label: 'All Stock Levels' },
                { value: 'low', label: 'Low Stock' },
                { value: 'out', label: 'Out of Stock' },
                { value: 'in', label: 'In Stock' },
              ]}
              onChange={(val) => {
                setStockFilter(val);
                setPage(1);
              }}
            />
          </div>
        </div>
      </div>

      {loading && products.length === 0 ? (
        <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-20 flex flex-col items-center justify-center">
          <Loader size="lg" />
          <p className="text-gray-500 mt-4 animate-pulse font-medium">
            Loading inventory...
          </p>
        </div>
      ) : products.length === 0 ? (
        <EmptyState
          title="No Products Found"
          description={
            search
              ? `No products match "${search}"`
              : 'Your inventory is currently empty.'
          }
          icon={<Package className="w-12 h-12" />}
        />
      ) : (
        <div className="bg-white rounded-2xl shadow-sm border border-gray-100 overflow-hidden flex flex-col">
          <div className="overflow-x-auto">
            <table className="w-full text-left">
              <thead className="bg-gray-50/50">
                <tr>
                  <th className="px-6 py-4 text-xs font-semibold text-gray-500 uppercase tracking-wider">
                    Product Name
                  </th>
                  <th className="px-6 py-4 text-xs font-semibold text-gray-500 uppercase tracking-wider">
                    Category
                  </th>
                  <th className="px-6 py-4 text-xs font-semibold text-gray-500 uppercase tracking-wider">
                    Price
                  </th>
                  <th className="px-6 py-4 text-xs font-semibold text-gray-500 uppercase tracking-wider">
                    Stock Level
                  </th>
                  <th className="px-6 py-4 text-xs font-semibold text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                </tr>
              </thead>
              <tbody
                className={`divide-y divide-gray-100 transition-opacity duration-200 ${loading ? 'opacity-50' : 'opacity-100'}`}
              >
                {(stockFilter
                  ? products.filter((p) => {
                      const status = getStockStatus(
                        p.stock,
                        p.lowStockThreshold
                      );
                      if (stockFilter === 'low') {
                        return status.label === 'Low Stock';
                      }
                      if (stockFilter === 'out') {
                        return status.label === 'Out of Stock';
                      }
                      if (stockFilter === 'in') {
                        return status.label === 'In Stock';
                      }
                      return true;
                    })
                  : products
                ).map((product) => {
                  const status = getStockStatus(
                    product.stock,
                    product.lowStockThreshold
                  );
                  const catId = getCategoryId(product);
                  const categoryName = categoryMap[catId] || 'N/A';

                  return (
                    <InventoryRow
                      key={product._id}
                      product={product}
                      categoryName={categoryName}
                      status={status}
                    />
                  );
                })}
              </tbody>
            </table>
          </div>

          {!stockFilter && pagination && pagination.pages > 1 && (
            <div className="p-4 border-t border-gray-50">
              <Pagination
                currentPage={page}
                totalPages={pagination.pages}
                onPageChange={setPage}
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Inventory;

import type { FC } from 'react';
import { useMemo } from 'react';
import {
  PieChart,
  Pie,
  Cell,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import type { Product } from '../../types/Product';
import type { Category } from '../../types/Category';

interface InventoryChartsProps {
  products: Product[];
  categories: Category[];
}

const STATUS_COLORS = {
  inStock: '#16a34a', // green-600
  lowStock: '#ea580c', // orange-600
  outOfStock: '#dc2626', // red-600
};

const CustomXAxisTick = ({
  x,
  y,
  payload,
}: {
  x: number;
  y: number;
  payload: { value: string };
}) => {
  const words = payload.value.split(' ');
  return (
    <g transform={`translate(${x},${y})`}>
      <text x={0} y={0} dy={25} textAnchor="middle" fill="#666" fontSize={12}>
        {words.map((word, index) => (
          <tspan key={index} x={0} dy={index === 0 ? 0 : 12}>
            {word}
          </tspan>
        ))}
      </text>
    </g>
  );
};

export const InventoryCharts: FC<InventoryChartsProps> = ({
  products,
  categories,
}) => {
  // 1. Stock Status Distribution (Pie Chart)
  const stockData = useMemo(() => {
    let inStock = 0;
    let lowStock = 0;
    let outOfStock = 0;

    products.forEach((p) => {
      const threshold = p.lowStockThreshold || 5;
      if (p.stock <= 0) {
        outOfStock++;
      } else if (p.stock <= threshold) {
        lowStock++;
      } else {
        inStock++;
      }
    });

    return [
      { name: 'In Stock', value: inStock, color: STATUS_COLORS.inStock },
      { name: 'Low Stock', value: lowStock, color: STATUS_COLORS.lowStock },
      {
        name: 'Out of Stock',
        value: outOfStock,
        color: STATUS_COLORS.outOfStock,
      },
    ].filter((d) => d.value > 0);
  }, [products]);

  // 2. Category Distribution (Bar Chart)
  const categoryData = useMemo(() => {
    const data = categories.map((cat) => {
      const catProducts = products.filter((p) => {
        let pCatId;
        // Check categoryId object/string
        if (typeof p.categoryId === 'object' && p.categoryId) {
          pCatId = (p.categoryId as any)._id;
        } else if (p.categoryId) {
          pCatId = p.categoryId;
        }
        // fallback to category field
        else if (typeof p.category === 'object' && p.category) {
          pCatId = (p.category as any)._id;
        } else {
          pCatId = p.category;
        }

        return pCatId === cat._id;
      });
      const totalStock = catProducts.reduce((acc, p) => acc + p.stock, 0);
      return {
        name: cat.name,
        stock: totalStock,
        count: catProducts.length,
      };
    });
    // Sort by stock desc and take top 8
    return data.sort((a, b) => b.stock - a.stock).slice(0, 8);
  }, [products, categories]);

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
      {/* Stock Status Pie Chart */}
      <div className="bg-white p-6 rounded-2xl shadow-sm border border-gray-100">
        <h3 className="text-lg font-bold text-gray-900 mb-4">Stock Overview</h3>
        <div className="h-[400px] w-full">
          <ResponsiveContainer width="100%" height="100%">
            <PieChart>
              <Pie
                data={stockData}
                cx="50%"
                cy="50%"
                innerRadius={60}
                outerRadius={100}
                paddingAngle={5}
                dataKey="value"
              >
                {stockData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip />
              <Legend verticalAlign="bottom" height={36} />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Category Stock Bar Chart */}
      <div className="bg-white p-6 rounded-2xl shadow-sm border border-gray-100">
        <h3 className="text-lg font-bold text-gray-900 mb-4">
          Stock by Category (Top 8)
        </h3>
        <div className="h-[400px] w-full">
          <ResponsiveContainer width="100%" height="100%">
            <BarChart
              data={categoryData}
              margin={{ top: 10, right: 10, left: -20, bottom: 80 }}
            >
              <CartesianGrid strokeDasharray="3 3" vertical={false} />
              <XAxis
                dataKey="name"
                interval={0}
                tick={<CustomXAxisTick x={0} y={0} payload={{ value: '' }} />}
                axisLine={false}
                tickLine={false}
              />
              <YAxis fontSize={12} tickLine={false} axisLine={false} />
              <Tooltip
                cursor={{ fill: 'transparent' }}
                contentStyle={{
                  borderRadius: '8px',
                  border: 'none',
                  boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)',
                }}
              />
              <Bar
                dataKey="stock"
                fill="#0ea5e9"
                radius={[4, 4, 0, 0]}
                name="Total Items"
              />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

import { useEffect, useState, useMemo } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import type { RootState } from '../../redux/rootReducer';
import {
  getAllOrders,
  changeOrderStatus,
} from '../../redux/actions/orderActions';

import OrderCard from '../../components/manager/orders/OrderCard';
import Loader from '../../components/common/Loader';
import Pagination from '../../components/common/Pagination';
import FilterSelect from '../../components/common/FilterSelect';
import { Package, SearchX } from 'lucide-react';
import { ORDER_STATUS_META } from '../../utils/orderStatus';

const PAGE_SIZE = 5;

const OrdersManagement = () => {
  const dispatch = useDispatch<any>();
  const { orders, loading, pagination } = useSelector(
    (state: RootState) => state.order
  );

  const [page, setPage] = useState(1);
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    dispatch(getAllOrders(page, PAGE_SIZE, statusFilter));
  }, [dispatch, page, statusFilter]);

  // Client-side filtering
  const filteredOrders = orders;

  const filterOptions = useMemo(() => {
    const options = Object.keys(ORDER_STATUS_META).map((status) => ({
      value: status,
      label: status,
    }));
    return [{ value: 'all', label: 'All Statuses' }, ...options];
  }, []);

  return (
    <div className="min-h-screen bg-transparent px-6 sm:px-12 lg:px-20 py-10 animate-fadeIn">
      <div className="max-w-[1400px] mx-auto space-y-10">
        {/* Header Section */}
        <div className="relative z-20 flex flex-col md:flex-row md:items-end justify-between gap-6">
          <div className="flex items-center gap-4">
            <div className="p-4 bg-white rounded-3xl shadow-sm text-primary border border-gray-100">
              <Package size={32} />
            </div>
            <div>
              <h1 className="text-3xl font-extrabold text-gray-900 tracking-tight">
                Order Management
              </h1>
              <p className="text-gray-500 font-medium">
                Review and update customer orders efficiently
              </p>
            </div>
          </div>

          <FilterSelect
            label="Filter by Status"
            value={statusFilter}
            options={filterOptions}
            onChange={setStatusFilter}
            className="md:w-64"
          />
        </div>

        {/* Content Area */}
        <div className="min-h-[500px] space-y-6">
          {loading && orders.length === 0 ? (
            <div className="h-[400px] flex items-center justify-center">
              <Loader />
            </div>
          ) : filteredOrders.length > 0 ? (
            <>
              <div className="grid grid-cols-1 gap-8">
                {filteredOrders.map((order, index) => (
                  <div
                    key={order._id}
                    className="animate-slideUp"
                    style={{ animationDelay: `${index * 50}ms` }}
                  >
                    <OrderCard
                      order={order}
                      onStatusChange={(id, status) =>
                        dispatch(changeOrderStatus(id, status))
                      }
                    />
                  </div>
                ))}
              </div>

              {/* Standard Pagination Component */}
              {pagination && pagination.pages > 1 && (
                <div className="pt-10 flex justify-center">
                  <Pagination
                    currentPage={page}
                    totalPages={pagination.pages}
                    onPageChange={(nextPage) => setPage(nextPage)}
                    isLoading={loading}
                  />
                </div>
              )}
            </>
          ) : (
            <div className="h-[400px] flex flex-col items-center justify-center text-center space-y-4 bg-white/40 backdrop-blur-md border border-dashed border-gray-300 rounded-3xl">
              <div className="p-4 bg-gray-100 rounded-full text-gray-400">
                {statusFilter === 'all' ? (
                  <Package size={48} />
                ) : (
                  <SearchX size={48} />
                )}
              </div>
              <div>
                <h3 className="text-xl font-bold text-gray-800">
                  {statusFilter === 'all'
                    ? 'No orders found'
                    : 'No matches found'}
                </h3>
                <p className="text-gray-500 max-w-xs mx-auto">
                  {statusFilter === 'all'
                    ? 'When customers place orders, they will appear here for you to manage.'
                    : `No orders currently match the "${statusFilter}" status on this page.`}
                </p>
                {statusFilter !== 'all' && (
                  <button
                    onClick={() => setStatusFilter('all')}
                    className="mt-4 text-primary font-semibold hover:underline cursor-pointer"
                  >
                    Clear filter
                  </button>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default OrdersManagement;

import { useEffect, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import {
  ArrowLeft,
  User,
  Calendar,
  Mail,
  Clock,
  CheckCircle2,
  AlertCircle,
  Hash,
} from 'lucide-react';

import Loader from '../../components/common/Loader';
import { selectSupportTickets } from '../../redux/selectors/supportSelectors';
import {
  fetchAllSupportTickets,
  updateSupportTicketStatus,
} from '../../redux/actions/supportActions';
import type { AppDispatch } from '../../redux/store';
import TicketStatusSelect from '../../components/manager/tickets/TicketStatusSelect';

export default function TicketDetails() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const dispatch = useDispatch<AppDispatch>();

  const tickets = useSelector(selectSupportTickets);

  // Find the ticket
  const ticket = useMemo(
    () => tickets.find((t) => t._id === id),
    [tickets, id]
  );

  useEffect(() => {
    if (!tickets.length) {
      dispatch(fetchAllSupportTickets());
    }
  }, [dispatch, tickets.length]);

  if (!ticket) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center p-8 bg-white/60 backdrop-blur-xl rounded-3xl border border-white/50 shadow-xl animate-fadeIn">
          <Loader />
          <p className="text-gray-500 mt-6 font-medium animate-pulse">
            Fetching ticket details...
          </p>
          <button
            onClick={() => navigate('/manager/support')}
            className="mt-8 px-6 py-2 rounded-full border border-primary/30 text-primary hover:bg-primary/5 transition-all text-sm font-semibold"
          >
            Back to Tickets
          </button>
        </div>
      </div>
    );
  }

  const handleStatusChange = (status: any) => {
    if (ticket._id) {
      dispatch(updateSupportTicketStatus(ticket._id, status));
    }
  };

  return (
    <div className="min-h-screen py-10 px-6 sm:px-12 lg:px-20">
      <div className="max-w-4xl mx-auto space-y-6">
        {/* BACK BUTTON */}
        <button
          onClick={() => navigate('/manager/support')}
          className="flex items-center gap-2 text-gray-600 hover:text-primary transition-colors group mb-4"
        >
          <div className="p-2 rounded-full bg-white/50 group-hover:bg-white shadow-sm transition-all">
            <ArrowLeft size={20} />
          </div>
          <span className="font-medium">Back to Tickets</span>
        </button>

        {/* MAIN CARD */}
        <div className="bg-white/70 backdrop-blur-xl border border-white/60 shadow-xl rounded-2xl relative">
          <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-primary to-emerald-400" />

          <div className="p-8">
            {/* HEADER */}
            <div className="flex flex-col md:flex-row md:items-start md:justify-between gap-6 mb-8">
              <div className="space-y-4">
                <div className="flex items-center gap-3">
                  <span className="px-3 py-1 rounded-full bg-primary/10 text-primary text-xs font-bold tracking-wider uppercase border border-primary/20">
                    Support Ticket
                  </span>
                  <span className="flex items-center gap-1 text-xs text-gray-400 font-mono bg-gray-50 px-2 py-1 rounded">
                    <Hash size={12} /> {ticket._id}
                  </span>
                </div>
                <h1 className="text-3xl font-bold text-gray-900 leading-tight">
                  {ticket.subject}
                </h1>
              </div>

              <div className="flex-shrink-0">
                <TicketStatusSelect
                  status={ticket.status}
                  onChange={handleStatusChange}
                  disabled={
                    ticket.status === 'resolved' || ticket.status === 'closed'
                  }
                />
              </div>
            </div>

            {/* DESCRIPTION */}
            <div className="bg-white/50 rounded-xl p-6 border border-white/50 shadow-inner mb-8">
              <h3 className="text-sm font-semibold text-gray-900 mb-3 flex items-center gap-2">
                <AlertCircle size={16} className="text-primary" />
                Issue Description
              </h3>
              <p className="text-gray-700 leading-relaxed whitespace-pre-wrap">
                {ticket.description}
              </p>
            </div>

            {/* META GRID */}
            <div className="grid md:grid-cols-2 gap-6">
              {/* CUSTOMER INFO */}
              <div className="p-5 rounded-xl bg-gradient-to-br from-orange-50 to-orange-100/50 border border-orange-100">
                <h3 className="text-sm font-bold text-orange-900 mb-4 flex items-center gap-2">
                  <User size={18} className="text-orange-600" />
                  Customer Details
                </h3>
                <div className="space-y-3">
                  <div className="flex items-center gap-3 text-sm text-gray-700">
                    <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center text-orange-500 shadow-sm">
                      <User size={16} />
                    </div>
                    <div>
                      <p className="text-xs text-gray-500">Name</p>
                      <p className="font-medium">
                        {ticket.user?.name || 'Guest User'}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-3 text-sm text-gray-700">
                    <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center text-orange-500 shadow-sm">
                      <Mail size={16} />
                    </div>
                    <div>
                      <p className="text-xs text-gray-500">Email</p>
                      <p className="font-medium break-all">
                        {ticket.user?.email}
                      </p>
                    </div>
                  </div>
                </div>
              </div>

              {/* TICKET INFO */}
              <div className="p-5 rounded-xl bg-gradient-to-br from-blue-50 to-blue-100/50 border border-blue-100">
                <h3 className="text-sm font-bold text-blue-900 mb-4 flex items-center gap-2">
                  <Clock size={18} className="text-blue-600" />
                  Ticket Information
                </h3>
                <div className="space-y-3">
                  <div className="flex items-center gap-3 text-sm text-gray-700">
                    <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center text-blue-500 shadow-sm">
                      <Calendar size={16} />
                    </div>
                    <div>
                      <p className="text-xs text-gray-500">Created At</p>
                      <p className="font-medium">
                        {new Date(ticket.createdAt).toLocaleString()}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-3 text-sm text-gray-700">
                    <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center text-blue-500 shadow-sm">
                      <CheckCircle2 size={16} />
                    </div>
                    <div>
                      <p className="text-xs text-gray-500">Assigned To</p>
                      <p className="font-medium">
                        {ticket.assignedManager?.name || 'Unassigned'}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
